INSERT INTO `k_blogs` VALUES ('64', '2015-01-07 14:03:35', '<p>&lt;!DOCTYPE html&gt;<br />\r\n&lt;html lang=&quot;en&quot;&gt;<br />\r\n&lt;head&gt;<br />\r\n&nbsp;&nbsp; &nbsp;&lt;meta charset=&quot;UTF-8&quot;&gt;<br />\r\n&nbsp;&nbsp; &nbsp;&lt;title&gt;Document&lt;/title&gt;<br />\r\n&lt;/head&gt;<br />\r\n&lt;body&gt;<br />\r\n&nbsp;&nbsp; &nbsp;&lt;script type=&quot;text/javascript&quot;&gt;<br />\r\n&nbsp; &nbsp;/*<br />\r\n&nbsp;&nbsp; &nbsp;// 作者 yanue<br />\r\n&nbsp;&nbsp; &nbsp;// 参数：<br />\r\n&nbsp;&nbsp; &nbsp;// startColor：开始颜色hex<br />\r\n&nbsp;&nbsp; &nbsp;// endColor：结束颜色hex<br />\r\n&nbsp; &nbsp; // step:几个阶级（几步）<br />\r\n&nbsp; &nbsp;*/<br />\r\n&nbsp; &nbsp;function gradientColor(startColor,endColor,step){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;startRGB = this.colorRgb(startColor);//转换为rgb数组模式<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;startR = startRGB[0];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;startG = startRGB[1];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;startB = startRGB[2];</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;endRGB = this.colorRgb(endColor);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;endR = endRGB[0];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;endG = endRGB[1];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;endB = endRGB[2];</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;sR = (endR-startR)/step;//总差值<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;sG = (endG-startG)/step;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;sB = (endB-startB)/step;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;var colorArr = [];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;for(var i=0;i&lt;step;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; //计算每一步的hex值&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var hex = this.colorHex(&#39;rgb(&#39;+parseInt((sR*i+startR))+&#39;,&#39;+parseInt((sG*i+startG))+&#39;,&#39;+parseInt((sB*i+startB))+&#39;)&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;hex = (i+2)+&quot;px &quot;+(i+3)+&quot;px &quot;+hex;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;colorArr.push(hex);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}document.write(colorArr)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;return colorArr;<br />\r\n&nbsp; &nbsp;}</p>\r\n\r\n<p>&nbsp; &nbsp;// 将hex表示方式转换为rgb表示方式(这里返回rgb数组模式)<br />\r\n&nbsp; &nbsp;gradientColor.prototype.colorRgb = function(sColor){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;var reg = /^#([0-9a-fA-f]{3}|[0-9a-fA-f]{6})$/;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;var sColor = sColor.toLowerCase();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;if(sColor &amp;&amp; reg.test(sColor)){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if(sColor.length === 4){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var sColorNew = &quot;#&quot;;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i=1; i&lt;4; i+=1){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sColorNew += sColor.slice(i,i+1).concat(sColor.slice(i,i+1));<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sColor = sColorNew;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;//处理六位的颜色值<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var sColorChange = [];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i=1; i&lt;7; i+=2){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sColorChange.push(parseInt(&quot;0x&quot;+sColor.slice(i,i+2)));<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return sColorChange;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return sColor;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp;};</p>\r\n\r\n<p>&nbsp; &nbsp;// 将rgb表示方式转换为hex表示方式<br />\r\n&nbsp; &nbsp;gradientColor.prototype.colorHex = function(rgb){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;var _this = rgb;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;var reg = /^#([0-9a-fA-f]{3}|[0-9a-fA-f]{6})$/;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;if(/^(rgb|RGB)/.test(_this)){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var aColor = _this.replace(/(?:(|)|rgb|RGB)*/g,&quot;&quot;).split(&quot;,&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var strHex = &quot;#&quot;;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i=0; i&lt;aColor.length; i++){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var hex = Number(aColor[i]).toString(16);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;hex = hex&lt;10 ? 0+&#39;&#39;+hex :hex;// 保证每个rgb的值为2位<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if(hex === &quot;0&quot;){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;hex += hex;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;strHex += hex;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if(strHex.length !== 7){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;strHex = _this;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return strHex;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}else if(reg.test(_this)){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var aNum = _this.replace(/#/,&quot;&quot;).split(&quot;&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if(aNum.length === 6){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return _this;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}else if(aNum.length === 3){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var numHex = &quot;#&quot;;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i=0; i&lt;aNum.length; i+=1){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;numHex += (aNum[i]+aNum[i]);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return numHex;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return _this;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp;}</p>\r\n\r\n<p>&nbsp; &nbsp;var gradient = new gradientColor(&#39;#86D2E7&#39;,&#39;#88DDF7&#39;,30);<br />\r\n&nbsp; &nbsp;/*alert(gradient);//控制台输出*/<br />\r\n&lt;/script&gt;<br />\r\n&lt;/body&gt;<br />\r\n&lt;/html&gt;</p>', '颜色梯度脚本', '9', '2015', 'Jan', '6', '1');
INSERT INTO `k_blogs` VALUES ('65', '2015-01-07 15:51:15', '<p>To untrack a&nbsp;<em>single</em>&nbsp;file that has already been added/initialized to your repository,&nbsp;<em>i.e.</em>, stop tracking the file but not delete it from your system use:&nbsp;<code>git rm --cached filename</code></p>\r\n\r\n<p>To untrack&nbsp;<em>every</em>&nbsp;file that is now in your&nbsp;<code>.gitignore</code>:</p>\r\n\r\n<p><strong>First commit any outstanding code changes</strong>, and then, run this command:</p>\r\n\r\n<pre>\r\n<code>git rm -r --cached .\r\n</code></pre>\r\n\r\n<p>This removes any changed files from the&nbsp;<em>index</em>(staging area), then just run:</p>\r\n\r\n<pre>\r\n<code>git add .\r\n</code></pre>\r\n\r\n<p>Commit it:</p>\r\n\r\n<pre>\r\n<code>git commit -m &quot;.gitignore is now working&quot;</code></pre>', 'git删除部分记录', '5', '2015', 'Jan', '7', '1');
INSERT INTO `k_blogs` VALUES ('66', '2015-01-12 19:45:18', '<p>js:</p>\r\n\r\n<p>(function($) {<br />\r\n&nbsp; &nbsp; $.fn.pager = function(options) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var opts = $.extend({}, $.fn.pager.defaults, options);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return this.each(function() {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(this).empty().append(renderpager(parseInt(options.pagenumber), parseInt(options.pagecount), options.buttonClickCallback));<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;.pages li&#39;).mouseover(function() { document.body.style.cursor = &quot;pointer&quot;; }).mouseout(function() { document.body.style.cursor = &quot;auto&quot;; });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });<br />\r\n&nbsp; &nbsp; };<br />\r\n&nbsp; &nbsp; function renderpager(pagenumber, pagecount, buttonClickCallback) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var $pager = $(&#39;&lt;ul class=&quot;pages&quot;&gt;&lt;/ul&gt;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $pager.append(renderButton(&#39;first&#39;, pagenumber, pagecount, buttonClickCallback)).append(renderButton(&#39;prev&#39;, pagenumber, pagecount, buttonClickCallback));<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var startPoint = 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var endPoint = 5;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (pagenumber &gt; 4) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startPoint = pagenumber - 2;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; endPoint = pagenumber + 2;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (endPoint &gt; pagecount) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startPoint = pagecount - 8;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; endPoint = pagecount;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (startPoint &lt; 1) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startPoint = 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; for (var page = startPoint; page &lt;= endPoint; page++) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var currentButton = $(&#39;&lt;li class=&quot;page-number&quot;&gt;&#39; + (page) + &#39;&lt;/li&gt;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function addInput(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;#pager&#39;).find(&#39;li&#39;).removeClass(&#39;pgCurrent&#39;).attr(&#39;style&#39;,&#39;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; currentButton.addClass(&#39;pgCurrent&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var &nbsp;pagenumberLiText = currentButton.text();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; currentButton.text(&#39;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; currentButton.append(&#39;&lt;input &nbsp;id=&quot;pgCurrentInput&quot; settext=&quot; &#39;+pagenumberLiText+&#39;&quot; readonly value=&quot; &#39;+pagenumberLiText+&#39;&quot;/&gt;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; page == pagenumber ? &nbsp;addInput(): currentButton.click(function() {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; buttonClickCallback(this.firstChild.data);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; page == pagenumber ? currentButton.click(function() {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;.pgCurrent #pgCurrentInput&#39;).removeAttr(&#39;readOnly&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;.pgCurrent&#39;).css({&#39;padding&#39;:&#39;0&#39;,&#39;width&#39;:&#39;46px&#39;});<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;#pgCurrentInput&#39;).css({&#39;height&#39;:&#39;24px&#39;,&#39;line-height&#39;:&#39;24px&#39;,&#39;width&#39;:&#39;46px&#39;,&#39;border&#39;:&#39;none&#39;});<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(&#39;#pgCurrentInput&#39;).bind(&#39;keypress&#39;,function(event){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(event.keyCode == &quot;13&quot;){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var textInput = $(&#39;#pgCurrentInput&#39;).val();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(textInput&gt;pagecount){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return false;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var settext = $(&#39;#pgCurrentInput&#39;).attr(&#39;settext&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var nowLiParent = $(&#39;#pgCurrentInput&#39;).parent();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nowLiParent.remove(&#39;input&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nowLiParent.text(settext)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&#39;#pager&#39;).find(&#39;li&#39;).removeClass(&#39;pgCurrent&#39;).attr(&#39;style&#39;,&#39;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var textInputInt = parseInt(textInput);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; buttonClickCallback(textInputInt);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; });}) : currentButton.click(function() { return false;});<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; currentButton.appendTo($pager);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; currentButton = null;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $pager.append(renderButton(&#39;next&#39;, pagenumber, pagecount, buttonClickCallback)).append(renderButton(&#39;last&#39;, pagenumber, pagecount, buttonClickCallback));<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return $pager;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; function renderButton(buttonLabel, pagenumber, pagecount, buttonClickCallback) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var $Button = $(&#39;&lt;li class=&quot;pgNext pager&#39;+buttonLabel+&#39;&quot;&gt;&lt;/li&gt;&#39;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var destPage = 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; switch (buttonLabel) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; case &quot;first&quot;:<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; destPage = 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; case &quot;prev&quot;:<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; destPage = pagenumber - 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; case &quot;next&quot;:<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; destPage = pagenumber + 1;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; case &quot;last&quot;:<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; destPage = pagecount;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (buttonLabel == &quot;first&quot; || buttonLabel == &quot;prev&quot;) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; pagenumber &lt;= 1 ? $Button.addClass(&#39;pgEmpty&#39;) : $Button.click(function() { buttonClickCallback(destPage); });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; else {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; pagenumber &gt;= pagecount ? $Button.addClass(&#39;pgEmpty&#39;) : $Button.click(function() { buttonClickCallback(destPage); });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return $Button;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; $.fn.pager.defaults = {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; pagenumber: 1,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; pagecount: 1<br />\r\n&nbsp; &nbsp; };<br />\r\n})(jQuery);</p>\r\n\r\n<p>css:</p>\r\n\r\n<p>#pager ul.pages {<br />\r\ndisplay:block;<br />\r\nborder:none;<br />\r\ntext-transform:uppercase;<br />\r\nfont-size:10px;<br />\r\nmargin:10px 0 50px;<br />\r\npadding:0;<br />\r\n}</p>\r\n\r\n<p>#pager ul.pages li {<br />\r\nlist-style:none;<br />\r\nfloat:left;</p>\r\n\r\n<p>text-decoration:none;<br />\r\nmargin:0 5px 0 0;<br />\r\npadding:5px;</p>\r\n\r\n<p>}</p>\r\n\r\n<p>#pager ul.pages li:hover {</p>\r\n\r\n<p>}</p>\r\n\r\n<p>#pager ul.pages li.pgEmpty {<br />\r\nborder:1px solid #eee;<br />\r\ncolor:#eee;<br />\r\n}</p>\r\n\r\n<p>#pager ul.pages li.pgCurrent {<br />\r\nborder:1px solid #003f7e;<br />\r\ncolor:#000;<br />\r\nfont-weight:700;<br />\r\nbackground-color:#eee;<br />\r\nborder-radius: 5px;<br />\r\nwidth:36px;</p>\r\n\r\n<p>}<br />\r\n#pgCurrentInput{<br />\r\n&nbsp; &nbsp; border:none;<br />\r\n&nbsp; &nbsp; background:#eee;<br />\r\n&nbsp; &nbsp; font-size: 10px;<br />\r\n&nbsp; &nbsp; width:36px;<br />\r\n&nbsp; &nbsp; border-radius: 5px;<br />\r\n&nbsp; &nbsp; text-align: center;<br />\r\n}<br />\r\n&nbsp; &nbsp; #pager ul.pages li.pgNext{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; height:28px;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; padding:0;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; margin:0;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; border:none;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; #pager ul.pages li.pagerfirst{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; background: url(1-1.png) no-repeat 50% 50%;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; width:30px;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; #pager ul.pages li.pagerprev{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; background: url(1-2.png) no-repeat 50% 50%;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; width:30px;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; #pager ul.pages li.pagernext{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; background: url(1-3.png) no-repeat 50% 50%;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; width:30px;<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; #pager ul.pages li.pagerlast{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; background: url(1-4.png) no-repeat 50% 50%;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; width:30px;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>html:</p>\r\n\r\n<p>&lt;!DOCTYPE html PUBLIC &quot;-//W3C//DTD XHTML 1.0 Transitional//EN&quot; &quot;http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd&quot;&gt;</p>\r\n\r\n<p>&lt;html xmlns=&quot;http://www.w3.org/1999/xhtml&quot;&gt;<br />\r\n&lt;head&gt;<br />\r\n&nbsp; &nbsp; &lt;title&gt;jQuery.pager.js Test&lt;/title&gt;<br />\r\n&nbsp; &nbsp; &lt;link href=&quot;Pager.css&quot; rel=&quot;stylesheet&quot; type=&quot;text/css&quot; /&gt;<br />\r\n&nbsp; &nbsp; &lt;script src=&quot;jquery.min.js&quot; type=&quot;text/javascript&quot;&gt;&lt;/script&gt;<br />\r\n&nbsp; &nbsp; &lt;script src=&quot;jquery.pager.js&quot; type=&quot;text/javascript&quot;&gt;&lt;/script&gt;<br />\r\n&nbsp; &nbsp; &lt;script type=&quot;text/javascript&quot; language=&quot;javascript&quot;&gt;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; $(document).ready(function() {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;#pager&quot;).pager({ pagenumber: 1, pagecount: 15, buttonClickCallback: PageClick });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; PageClick = function(pageclickednumber) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;#pager&quot;).pager({ pagenumber: pageclickednumber, pagecount: 15, buttonClickCallback: PageClick });</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;#result&quot;).html(&quot;Clicked Page &quot; + pageclickednumber);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; &lt;/script&gt;<br />\r\n&lt;/head&gt;<br />\r\n&lt;body&gt;<br />\r\n&lt;h1 id=&quot;result&quot;&gt;Click the pager below.&lt;/h1&gt;<br />\r\n&lt;div id=&quot;pager&quot; &gt;&lt;/div&gt;<br />\r\n&lt;/body&gt;<br />\r\n&lt;/html&gt;</p>', '可输入分页插件改装', '9', '2015', 'Jan', '12', '1');
INSERT INTO `k_blogs` VALUES ('67', '2015-01-12 19:53:24', '<p>&nbsp; &nbsp; &nbsp; &nbsp; var a = &quot;这是一个从后端传到前端的变量&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;fs.readFile(dirnameUp + &#39;/views/index.html&#39;, &#39;utf8&#39;,function(error, content) {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (error) console.log(error);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;content = content.replace(&quot;&lt;%=a %&gt;&quot;, a);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;res.writeHead(200, {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;Content-Type&#39;: &#39;text/html&#39;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;res.end(content,&#39;utf-8&#39;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;});</p>', '&lt;%=a %&gt;如何实现的', '1', '2015', 'Jan', '11', '1');
INSERT INTO `k_blogs` VALUES ('68', '2015-01-23 16:59:14', '<p>有其他人封装好的脚本</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nfunction getClientIp(req) {\r\n    var ipAddress;\r\n    var forwardedIpsStr = req.header(&#39;x-forwarded-for&#39;); \r\n    if (forwardedIpsStr) {\r\n        var forwardedIps = forwardedIpsStr.split(&#39;,&#39;);\r\n        ipAddress = forwardedIps[0];\r\n    }\r\n    if (!ipAddress) {\r\n        ipAddress = req.connection.remoteAddress;\r\n    }\r\n    return ipAddress;\r\n};\r\n</pre>\r\n\r\n<p>还要注意的就是nginx反向代理的时候需要添加参数：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nlocation / {\r\nproxy_pass //代理网址\r\n# Forward the user&#39;s IP address to Rails\r\nproxy_set_header X-Real-IP $remote_addr;\r\n# needed for HTTPS\r\n# proxy_set_header X_FORWARDED_PROTO https;\r\nproxy_set_header X-Forwarded-For $remote_addr;\r\nproxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;\r\nproxy_set_header Host $host;\r\nproxy_redirect off;\r\n# NginxHttpRealIpModule\r\nset_real_ip_from 192.168.1.0/24;\r\nset_real_ip_from 192.168.2.1;\r\nreal_ip_header X-Real-IP;\r\n}</pre>\r\n\r\n<p>看到一个别人写的，也粘出来：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nvar os = require(&#39;os&#39;);\r\nvar ifaces = os.networkInterfaces();\r\nObject.keys(ifaces).forEach(function (ifname) {\r\n    var alias = 0;\r\n    ifaces[ifname].forEach(function (iface) {\r\n        if (&#39;IPv4&#39; !== iface.family || iface.internal !== false) {\r\n            // skip over internal (i.e. 127.0.0.1) and non-ipv4 addresses\r\n            return;\r\n        }\r\n        if (alias &gt;= 1) {\r\n            // this single interface has multiple ipv4 addresses\r\n            console.log(ifname + &#39;:&#39; + alias, iface.address);\r\n        } else {\r\n            // this interface has only one ipv4 adress\r\n            console.log(ifname, iface.address);\r\n        }\r\n    });\r\n});</pre>', 'node.js返回客户端ip', '6', '2015', 'Jan', '23', '1');
INSERT INTO `k_blogs` VALUES ('69', '2015-01-26 17:53:57', '<p>我们把对IE的判断放在第一，因为IE的用户最多，其次是判断Firefox。按使用者多少的顺序来判断浏览器类型，可以提高判断效率，少做无用功。之所以将Chrome放在第三判断，是因为我们预测Chrome很快会成为市场占有率第三的浏览器。其中，在分析浏览器版本时，用到了正则表达式来析取其中的版本信息。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n&nbsp;   var Sys = {};\r\n&nbsp; &nbsp; var ua = navigator.userAgent.toLowerCase();\r\n&nbsp; &nbsp; window.ActiveXObject ? Sys.ie = ua.match(/msie ([\\d.]+)/)[1] :\r\n&nbsp; &nbsp; document.getBoxObjectFor ? Sys.firefox = ua.match(/firefox\\/([\\d.]+)/)[1] :\r\n&nbsp; &nbsp; window.MessageEvent &amp;&amp; !document.getBoxObjectFor ? Sys.chrome = ua.match(/chrome\\/([\\d.]+)/)[1] :\r\n&nbsp; &nbsp; window.opera ? Sys.opera = ua.match(/opera.([\\d.]+)/)[1] :\r\n&nbsp; &nbsp; window.openDatabase ? Sys.safari = ua.match(/version\\/([\\d.]+)/)[1] : 0;\r\n&nbsp; &nbsp; //以下进行测试\r\n&nbsp; &nbsp; if(Sys.ie) document.write(&#39;IE: &#39;+Sys.ie);\r\n&nbsp; &nbsp; if(Sys.firefox) document.write(&#39;Firefox: &#39;+Sys.firefox);\r\n&nbsp; &nbsp; if(Sys.chrome) document.write(&#39;Chrome: &#39;+Sys.chrome);\r\n&nbsp; &nbsp; if(Sys.opera) document.write(&#39;Opera: &#39;+Sys.opera);\r\n&nbsp; &nbsp; if(Sys.safari) document.write(&#39;Safari: &#39;+Sys.safari);\r\n&lt;/script&gt;</pre>\r\n\r\n<p>但是上面的代码有一个问题，就是检测ie浏览器，只能检测到ie7+版本，所以还有这个可以判断ie5、ie6的示例：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n&nbsp;&nbsp; &nbsp;if(!+&#39;\\v1&#39; &amp;&amp; !&#39;1&#39;[0]){\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(&quot;ie6或ie7&quot;)\r\n&nbsp;&nbsp; &nbsp;}\r\n&lt;/script&gt;</pre>\r\n\r\n<p>使用不同特征来判断浏览器的方法，虽然在速度上比用正则表达式分析userAgent要来的快，不过这些特征可能会随浏览器版本而变化。比如，一种浏览器本来独有的特性取得了市场上的成功，其他浏览器也就可能跟着加入该特性，从而使该浏览器的独有特征消失，导致我们的判断失败。因此，相对比较保险的做法是通过解析userAgent中的特征来判断浏览器类型。何况，反正判断版本信息也需要解析浏览器的userAgent的。</p>\r\n\r\n<p>通过分析各类浏览器的userAgent信息，不难得出分辨各类浏览器及其版本的正则表达式。而且，对浏览器类型的判断和版本的判断完全可以合为一体地进行。于是，我们可以写出下面的代码：&nbsp;</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n &lt;script type=&quot;text/javascript&quot;&gt;\r\n&nbsp; &nbsp; var Sys = {};\r\n&nbsp; &nbsp; var ua = navigator.userAgent.toLowerCase();\r\n&nbsp; &nbsp; var s;\r\n&nbsp; &nbsp; (s = ua.match(/msie ([\\d.]+)/)) ? Sys.ie = s[1] :\r\n&nbsp; &nbsp; (s = ua.match(/firefox\\/([\\d.]+)/)) ? Sys.firefox = s[1] :\r\n&nbsp; &nbsp; (s = ua.match(/chrome\\/([\\d.]+)/)) ? Sys.chrome = s[1] :\r\n&nbsp; &nbsp; (s = ua.match(/opera.([\\d.]+)/)) ? Sys.opera = s[1] :\r\n&nbsp; &nbsp; (s = ua.match(/version\\/([\\d.]+).*safari/)) ? Sys.safari = s[1] : 0;\r\n&nbsp; &nbsp; //以下进行测试\r\n&nbsp; &nbsp; if (Sys.ie) document.write(&#39;IE: &#39; + Sys.ie);\r\n&nbsp; &nbsp; if (Sys.firefox) document.write(&#39;Firefox: &#39; + Sys.firefox);\r\n&nbsp; &nbsp; if (Sys.chrome) document.write(&#39;Chrome: &#39; + Sys.chrome);\r\n&nbsp; &nbsp; if (Sys.opera) document.write(&#39;Opera: &#39; + Sys.opera);\r\n&nbsp; &nbsp; if (Sys.safari) document.write(&#39;Safari: &#39; + Sys.safari);\r\n&lt;/script&gt;</pre>\r\n\r\n<p>判断某种浏览器只需用if(Sys.ie)或 if(Sys.firefox)等形式，而判断浏览器版本只需用if(Sys.ie == &#39;8.0&#39;)或if(Sys.firefox == &#39;3.0&#39;)等形式。</p>\r\n\r\n<p>最后附上一些浏览器的介绍：</p>\r\n\r\n<p><strong>IE</strong></p>\r\n\r\n<p>只有IE支持创建ActiveX控件，因此她有一个其他浏览器没有的东西，就是ActiveXObject函数。只要判断window对象存在 ActiveXObject函数，就可以明确判断出当前浏览器是IE。而IE各个版本典型的userAgent如下：</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/4.0 (compatible; MSIE 8.0; Windows NT 6.0)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/4.0 (compatible; MSIE 7.0; Windows NT 5.2)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.1)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/4.0 (compatible; MSIE 5.0; Windows NT)</p>\r\n\r\n<p>其中，版本号是MSIE之后的数字。</p>\r\n\r\n<p><strong>Firefox</strong></p>\r\n\r\n<p>Firefox中的DOM元素都有一个getBoxObjectFor函数，用来获取该DOM元素的位置和大小（IE对应的中是 getBoundingClientRect函数）。这是Firefox独有的，判断它即可知道是当前浏览器是Firefox。Firefox几个版本的 userAgent大致如下：</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Windows; U; Windows NT 5.2) Gecko/2008070208 Firefox/3.0.1<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Windows; U; Windows NT 5.1) Gecko/20070309 Firefox/2.0.0.3<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Windows; U; Windows NT 5.1) Gecko/20070803 Firefox/1.5.0.12</p>\r\n\r\n<p>其中，版本号是Firefox之后的数字。</p>\r\n\r\n<p><strong>Opera</strong></p>\r\n\r\n<p>Opera提供了专门的浏览器标志，就是window.opera属性。Opera典型的userAgent如下：</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Opera/9.27 (Windows NT 5.2; U; zh-cn)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Opera/8.0 (Macintosh; PPC Mac OS X; U; en)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Macintosh; PPC Mac OS X; U; en) Opera 8.0</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其中，版本号是靠近Opera的数字。</p>\r\n\r\n<p><strong>Safari</strong></p>\r\n\r\n<p>Safari浏览器中有一个其他浏览器没有的openDatabase函数，可做为判断Safari的标志。Safari典型的userAgent如下：</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Windows; U; Windows NT 5.2) AppleWebKit/525.13 (KHTML, like Gecko) Version/3.1 Safari/525.13<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (iPhone; U; CPU like Mac OS X) AppleWebKit/420.1 (KHTML, like Gecko) Version/3.0 Mobile/4A93 Safari/419.3</p>\r\n\r\n<p>其版本号是Version之后的数字。</p>\r\n\r\n<p><strong>Chrome</strong></p>\r\n\r\n<p>Chrome有一个MessageEvent函数，但Firefox也有。不过，好在Chrome并没有Firefox的getBoxObjectFor 函数，根据这个条件还是可以准确判断出Chrome浏览器的。目前，Chrome的userAgent是：</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mozilla/5.0 (Windows; U; Windows NT 5.2) AppleWebKit/525.13 (KHTML, like Gecko) Chrome/0.2.149.27 Safari/525.13</p>\r\n\r\n<p>其中，版本号在Chrome只后的数字。</p>\r\n\r\n<p>有趣的是，Chrome的userAgent还包含了Safari的特征，也许这就是Chrome可以运行所有Apple浏览器应用的基础吧。</p>\r\n\r\n<p>--------------------------------------分割线------------------------------------</p>\r\n\r\n<p>然后我又在其他博客上看到了这个：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script language=&quot;JavaScript&quot;&gt;\r\n&nbsp;   //验证浏览器及其版本是通过浏览器的user-agent字符串来检测的,首先要获取user-agent字符串及浏览器版本\r\n&nbsp;&nbsp; &nbsp;var sUserAgent = navigator.userAgent;\r\n&nbsp;&nbsp; &nbsp;//parseFloat 运行时逐个读取字符串中的字符，当他发现第一个非数字符是就停止\r\n&nbsp;&nbsp; &nbsp;var fAppVersion = parseFloat(navigator.appVersion);\r\n\r\n&nbsp;   //接着需要提供一个版本比较函数，方便我们对小数版本号进行比对\r\n&nbsp;&nbsp; &nbsp;function compareVersions(sVersion1,sVersion2){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var aVersion1 = sVersion1.split(&quot;.&quot;);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var aVersion2 = sVersion2.split(&quot;.&quot;);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;if(aVersion1.length &gt; aVersion2.length){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i = 0 ;i &lt; aVersion1.length - aVersion2.length ; i++){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;aVersion2.push(&quot;0&quot;);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}else if(aVersion1.length &lt; aVersion2.length){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for(var i = 0 ;i &lt; aVersion2.length - aVersion1.length ; i++){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;aVersion2.push(0);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;for(var i = 0 ;i &lt; aVersion1.length ; i++){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var iVal1 = parseInt(aVersion1[i],10);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var iVal2 = parseInt(aVersion2[i],10);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if(iVal1 &lt; iVal2){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return -1;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}else if(iVal1 &gt; iVal2){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return 1;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;return 0;\r\n&nbsp;&nbsp; &nbsp;}\r\n\r\n&nbsp;   //Opera 浏览器检测\r\n&nbsp; &nbsp; var isOpera = sUserAgent.indexOf(&quot;Opera&quot;) &gt; -1 ;\r\n&nbsp;&nbsp; &nbsp;var isMinOpera4 = isMinOpera5 = isMinOpera6 = isMinOpera7 = isMinOpera7_5 = false;\r\n&nbsp;&nbsp; &nbsp;if(isOpera){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var fOperaVersion ; //定义Opera浏览器版本\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;//首先检测Opera是否进行了伪装\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;if(navigator.appName == &#39;Opera&#39;){\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;fOperaVersion = fAppVersion;//如果没有进行伪装，则直接后去版本号\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}else{\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;var reOperaVersion = new RegExp(&quot;Opera (//d+//.//d+)&quot;);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;reOperaVersion.test(sUserAgent);//使用正则表达式的test方法测试并将版本号保存在RegExp.$1中\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;fOperaVersion = parseFloat(RegExp[&#39;$1&#39;]);\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;isMinOpera4 = fOperaVersion &gt;= 4;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;isMinOpera5 = fOperaVersion &gt;= 5;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;isMinOpera6 = fOperaVersion &gt;= 6;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;isMinOpera7 = fOperaVersion &gt;= 7;\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;isMinOpera7_5 = fOperaVersion &gt;= 7.5;\r\n&nbsp;&nbsp; &nbsp;} &nbsp; \r\n&nbsp;   //Google Chrome 浏览器检测\r\n    var isChrome = sUserAgent.indexOf(&quot;Chrome&quot;) &gt; -1 ;\r\n    var isMinChrome2 = false;//这没有检测完，如需要可针对Chrome进行检测\r\n    if(isChrome){\r\n    &nbsp; &nbsp; var reChorme = new RegExp(&quot;Chrome///(//d+//.//d+(?://.//d+)?)&quot;);\r\n    &nbsp; &nbsp; reChorme.test(sUserAgent);\r\n    &nbsp; &nbsp; isMinChrome2 = compareVersions(RegExp[&quot;$1&quot;],&quot;2.0&quot;) &gt;= 0;\r\n    }\r\n\r\n    //排除Chrome信息，因为在Chrome的user-agent字符串中会出现Konqueror/Safari的关键字\r\n    var isKHTML = (sUserAgent.indexOf(&quot;KHTML&quot;) &gt; -1\r\n                    || sUserAgent.indexOf(&quot;Konqueror&quot;) &gt; -1\r\n                    || sUserAgent.indexOf(&quot;AppleWebKit&quot;) &gt; -1) &amp;&amp; !isChrome ;\r\n    var isMinSfari1 = isMinSfari1_2 = false;\r\n    var isMinKonq2_2 = isMinKonq3 = isMinKonq3_1 = isMinKonq3_2 = false;\r\n    if(isKHTML){//判断是否基于KHTML，如果时的话在继续判断属于何种KHTML浏览器\r\n        var isSafari1 = sUserAgent.indexOf(&quot;AppleWebKit&quot;) &gt; -1;\r\n        var isKonq = sUserAgent.indexOf(&quot;Konqueror&quot;) &gt; -1;\r\n        if(isSafari1){\r\n            var reAppleWebKit = new RegExp(&quot;AppleWebKit///(//d+(?://.//d*)?)&quot;);\r\n            reAppleWebKit.test(sUserAgent);\r\n            var fAppleWebKitVersion = parseFloat(RegExp[&quot;$1&quot;]);\r\n            isMinSfari1 = fAppleWebKitVersion &gt;= 85;\r\n            isMinSfari1_2 = fAppleWebKitVersion &gt;= 124;\r\n        }else if(isKonq){\r\n            var reKong = new RegExp(&quot;Konqueror///(//d+(?://.//d+(?://.//d)?)?)&quot;);\r\n            reKong.test(sUserAgent);\r\n            isMinKonq2_2 = compareVersions(RegExp[&quot;$1&quot;],&quot;2.2&quot;) &gt;= 0;\r\n            isMinKonq3 = compareVersions(RegExp[&quot;$1&quot;],&quot;3.0&quot;) &gt;= 0;\r\n            isMinKonq3_1 = compareVersions(RegExp[&quot;$1&quot;],&quot;3.1&quot;) &gt;= 0;\r\n            isMinKonq3_2 = compareVersions(RegExp[&quot;$1&quot;],&quot;3.2&quot;) &gt;= 0;\r\n        }\r\n    }\r\n\r\n    //IE 浏览器检测\r\n    // !isOpera 避免是由Opera伪装成的IE\r\n    var isIE = sUserAgent.indexOf(&quot;compatible&quot;) &gt; -1 &amp;&amp; sUserAgent.indexOf(&quot;MSIE&quot;) &gt; -1 &amp;&amp; !isOpera;\r\n    var isMinIE4 = isMinIE5 = isMinIE5_5 = isMinIE6 = isMinIE7 = isMinIE8 = false;\r\n    if(isIE){\r\n        var reIE = new RegExp(&quot;MSIE (//d+//.//d+);&quot;);\r\n        reIE.test(sUserAgent);\r\n        var fIEVersion = parseFloat(RegExp[&quot;$1&quot;]);\r\n        isMinIE4 = fIEVersion &gt;= 4;\r\n        //alert(&#39;isMinIE4=&#39;+isMinIE4);\r\n        isMinIE5 = fIEVersion &gt;= 5;\r\n        //alert(&#39;isMinIE5=&#39;+isMinIE5);\r\n        isMinIE5_5 = fIEVersion &gt;= 5.5;\r\n        //alert(&#39;isMinIE5_5=&#39;+isMinIE5_5);\r\n        isMinIE6 = fIEVersion &gt;= 6;\r\n        //alert(&#39;isMinIE6=&#39;+isMinIE6);\r\n        isMinIE7 = fIEVersion &gt;= 7;\r\n        //alert(&#39;isMinIE7=&#39;+isMinIE7);\r\n        isMinIE8 = fIEVersion &gt;= 8;\r\n        //alert(&#39;isMinIE8=&#39;+isMinIE8);\r\n    }\r\n\r\n    //Mozilla FireFox 浏览器检测\r\n    //排除Chrome 及Konqueror/Safari的伪装\r\n    var isMoz = sUserAgent.indexOf(&quot;Gecko&quot;) &gt; -1 &amp;&amp; !isChrome &amp;&amp;!isKHTML;\r\n    var isMinMoz1 = isMinMoz1_4 = isMinMoz1_5 = false;\r\n    if(isMoz){\r\n    &nbsp; &nbsp; var reMoz = new RegExp(&quot;rv:(//d+//.//d+(?://.//d+)?)&quot;);\r\n    &nbsp; &nbsp; reMoz.test(sUserAgent);\r\n    &nbsp; &nbsp; isMinMoz1 = compareVersions(RegExp[&quot;$1&quot;],&quot;1.0&quot;) &gt;= 0;\r\n    &nbsp; &nbsp; isMinMoz1_4 = compareVersions(RegExp[&quot;$1&quot;],&quot;1.4&quot;) &gt;= 0;\r\n    &nbsp; &nbsp; isMinMoz1_5 = compareVersions(RegExp[&quot;$1&quot;],&quot;1.5&quot;) &gt;= 0;\r\n    }\r\n\r\n    alert(navigator.userAgent);\r\n &lt;/script&gt;</pre>\r\n\r\n<p>简单有效的区分ie浏览器版本，但是遗憾的是还是不支持ie7以下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n    var browser=navigator.appName\r\n    var b_version=navigator.appVersion\r\n    var version=b_version.split(&quot;;&quot;);\r\n    var trim_Version=version[1].replace(/[ ]/g,&quot;&quot;);\r\n    if(browser==&quot;Microsoft Internet Explorer&quot; &amp;&amp; trim_Version==&quot;MSIE6.0&quot;)\r\n    {\r\n    alert(&quot;IE 6.0&quot;);\r\n    }\r\n    else if(browser==&quot;Microsoft Internet Explorer&quot; &amp;&amp; trim_Version==&quot;MSIE7.0&quot;)\r\n    {\r\n    alert(&quot;IE 7.0&quot;);\r\n    window.location.href=&quot;http://www.baidu.com&quot;;\r\n    }\r\n    else if(browser==&quot;Microsoft Internet Explorer&quot; &amp;&amp; trim_Version==&quot;MSIE8.0&quot;)\r\n    {\r\n    alert(&quot;IE 8.0&quot;);\r\n    }\r\n    else if(browser==&quot;Microsoft Internet Explorer&quot; &amp;&amp; trim_Version==&quot;MSIE9.0&quot;)\r\n    {\r\n    alert(&quot;IE 9.0&quot;);\r\n    }\r\n&lt;/script&gt;</pre>\r\n\r\n<p>大同小异：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n    if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion .split(&quot;;&quot;)[1].replace(/[ ]/g,&quot;&quot;)==&quot;MSIE6.0&quot;)\r\n    {\r\n    alert(&quot;IE 6.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion .split(&quot;;&quot;)[1].replace(/[ ]/g,&quot;&quot;)==&quot;MSIE7.0&quot;)\r\n    {\r\n    alert(&quot;IE 7.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion .split(&quot;;&quot;)[1].replace(/[ ]/g,&quot;&quot;)==&quot;MSIE8.0&quot;)\r\n    {\r\n    alert(&quot;IE 8.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion .split(&quot;;&quot;)[1].replace(/[ ]/g,&quot;&quot;)==&quot;MSIE9.0&quot;)\r\n    {\r\n    alert(&quot;IE 9.0&quot;);\r\n    }\r\n&lt;/script&gt;</pre>\r\n\r\n<p>还有：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n    if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion.match(/7./i)==&quot;7.&quot;)\r\n    {\r\n    alert(&quot;IE 7.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion.match(/8./i)==&quot;8.&quot;)\r\n    {\r\n    alert(&quot;IE 8.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot; &amp;&amp; navigator.appVersion.match(/9./i)==&quot;9.&quot;)\r\n    {\r\n    alert(&quot;IE 9.0&quot;);\r\n    }\r\n    else if(navigator.appName == &quot;Microsoft Internet Explorer&quot;)\r\n    {\r\n    alert(&quot;IE 6.0&quot;);\r\n    }\r\n&lt;/script&gt;</pre>\r\n\r\n<p>--------------------------------------分割线------------------------------------</p>\r\n\r\n<p>判断ie版本：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;script&gt;\r\n    function Browser() {\r\n       var ua, s, i;\r\n       this.isIE     = false;   // Internet Explorer\r\n       this.isNS     = false;   // Netscape\r\n       this.version = null;\r\n       ua = navigator.userAgent;\r\n       s = &quot;MSIE&quot;;\r\n       if ((i = ua.indexOf(s)) &gt;= 0) {\r\n         this.isIE = true;\r\n         this.version = parseFloat(ua.substr(i + s.length));\r\n         return;\r\n       }\r\n       s = &quot;Netscape6/&quot;;\r\n       if ((i = ua.indexOf(s)) &gt;= 0) {\r\n         this.isNS = true;\r\n         this.version = parseFloat(ua.substr(i + s.length));\r\n         return;\r\n       }\r\n       // Treat any other &quot;Gecko&quot; browser as NS 6.1.\r\n\r\n       s = &quot;Gecko&quot;;\r\n       if ((i = ua.indexOf(s)) &gt;= 0) {\r\n         this.isNS = true;\r\n         this.version = 6.1;\r\n         return;\r\n       }\r\n    }\r\n    var browser = new Browser();\r\n    console.log(browser);\r\n&lt;/script&gt;</pre>', '浏览器版本检测', '9', '2015', 'Jan', '26', '1');
INSERT INTO `k_blogs` VALUES ('70', '2015-02-06 13:05:30', '<h2>1、Chrome下absolute绝对定位元素部分display属性值不渲染bug</h2>\r\n\r\n<p>Chrome浏览器下，绝对定位元素，其&nbsp;<code>display</code>&nbsp;值在&nbsp;<code>inline/inline-block</code>&nbsp;和&nbsp;<code>block</code>&nbsp;之间切换时候，是不重新渲染的。</p>\r\n\r\n<p>解决方案：</p>\r\n\r\n<p>遇到一些交互渲染bug时候，不妨触发浏览器的重绘（webkit/blink内核渲染bug修复可使用<kbd>visibility:inherit/visible</kbd>或者<kbd>-webkit-transform: translateZ(0);</kbd>）</p>\r\n\r\n<h2>2、Chrome下absolute绝对定位元素具有overflow属性的块状子元素visibility hover不渲染bug</h2>\r\n\r\n<p>Chrome浏览器下，绝对定位元素，同时visibility:hidden时候，如果子元素具有块状水平，同时设置了overflow:hidden; 则父元素hover时候，无法让子元素visibility:visible渲染生效</p>\r\n\r\n<p>解决方案：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-webkit-transform: translateZ(0);</pre>\r\n\r\n<h2>3、Chrome渲染Transition时页面闪动bug</h2>\r\n\r\n<p>推测为Chrome在初始渲染CSS动画时产生的bug</p>\r\n\r\n<p>解决方案：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-webkit-backface-visibility: hidden;（设置进行转换的元素的背面在面对用户时是否可见：隐藏）\r\n-webkit-transform-style: preserve-3d; （设置内嵌的元素在 3D 空间如何呈现：保留 3D ）</pre>\r\n\r\n<h2>4、Chrome浏览器下，随机渲染bug</h2>\r\n\r\n<p>解决方案：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-webkit-transform: translateZ(0);</pre>\r\n\r\n<p>&nbsp;</p>', 'chrome专题', '24', '2015', 'Jan', '27', '1');
INSERT INTO `k_blogs` VALUES ('71', '2015-01-27 23:44:53', '<p>更新nginx的配置文件如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nupstream nginx.shiyeshu.com { \r\n        server localhost:3000; \r\n} \r\nupstream nginx.mysql.shiyeshu.com { \r\n       server localhost:8080; \r\n}\r\nupstream nginx.npm.shiyeshu.com { \r\n       server localhost:7002; \r\n}\r\nupstream nginx.cnpm.shiyeshu.com {\r\n       server localhost:7001;\r\n} \r\nserver { \r\n    listen       80; \r\n    server_name  shiyeshu.com www.shiyeshu.com; \r\n    location / { \r\n                proxy_pass http://nginx.shiyeshu.com;\r\n# Forward the user&#39;s IP address to Rails\r\nproxy_headers_hash_max_size 51200;\r\nproxy_headers_hash_bucket_size 6400;\r\nproxy_set_header X-Real-IP $remote_addr;\r\n# needed for HTTPS\r\n# proxy_set_header X_FORWARDED_PROTO https;\r\nproxy_set_header X-Forwarded-For $remote_addr;\r\nproxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;\r\nproxy_set_header Host $host;\r\nproxy_redirect off; \r\n    } \r\n    error_page  404              /404.html; \r\n    location = /404.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n    error_page   500 502 503 504  /50x.html; \r\n    location = /50x.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n} \r\n\r\nserver { \r\n    listen       80; \r\n    server_name  mysql.shiyeshu.com; \r\n    location / { \r\n         proxy_pass http://nginx.mysql.shiyeshu.com; \r\n    } \r\n    error_page  404              /404.html; \r\n    location = /404.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n    error_page   500 502 503 504  /50x.html; \r\n    location = /50x.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n} \r\nserver { \r\n    listen       80; \r\n    server_name  npm.shiyeshu.com; \r\n    location / { \r\n         proxy_pass http://nginx.npm.shiyeshu.com; \r\n    } \r\n    error_page  404              /404.html; \r\n    location = /404.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n    error_page   500 502 503 504  /50x.html; \r\n    location = /50x.html { \r\n        root   /usr/share/nginx/html; \r\n    } \r\n}\r\nserver {\r\n    listen       80;\r\n    server_name  cnpm.shiyeshu.com;\r\n    location / {\r\n         proxy_pass http://nginx.cnpm.shiyeshu.com;\r\n    }\r\n    error_page  404              /404.html;\r\n    location = /404.html {\r\n        root   /usr/share/nginx/html;\r\n    }\r\n    error_page   500 502 503 504  /50x.html;\r\n    location = /50x.html {\r\n        root   /usr/share/nginx/html;\r\n    }\r\n}</pre>', '更新nginx配置文件', '18', '2015', 'Jan', '23', '1');
INSERT INTO `k_blogs` VALUES ('72', '2015-01-28 10:31:28', '<p>node.js发布脚本示例：</p>\r\n\r\n<pre>\r\n#!/bin/sh\r\n#\r\nif [ ! -z &quot;$2&quot; ]; then\r\n   if [ ! -d &quot;$1/$2&quot; ]; then\r\n      mkdir -p &quot;$1/$2&quot;\r\n   else\r\n      echo &quot;[info] shudown app $2&quot;\r\n      cd $1/$2\r\n      forever stop\r\n   \r\n      server_backup_dir=$1/$2.bak\r\n      if [ -d $server_backup_dir ]; then\r\n         rm -rf $server_backup_dir\r\n      fi\r\n      mkdir $server_backup_dir\r\n\r\n      echo &quot;[info] backup files in $1/$2&quot;\r\n      tar -czvf $2.bak.tgz $1/$2 &gt; /dev/null 2&gt;&amp;1\r\n      mv $2.bak.tgz $server_backup_dir\r\n      echo &quot;[info] remove old files&quot;\r\n   fi\r\n   cd $1\r\n   tar -xzf $2.tar.gz -C $1/$2/\r\n   echo &quot;[info] start app $2&quot;\r\n   cd $1/$2\r\n   forever start bin/www\r\nfi</pre>', 'node.js发布脚本示例', '6', '2015', 'Jan', '9', '1');
INSERT INTO `k_blogs` VALUES ('73', '2015-01-29 12:16:30', '<p>当做一些动态特效发生js代码无效时，分别在各个浏览器下测试，发现ie浏览器下可以动，chrome和火狐均不支持。</p>\r\n\r\n<p>原因是&lt;!DOCTYPE html&gt;为DOCTYPE申明，网页加入该代码意味着，js的编写必须符合规范。</p>\r\n\r\n<p>加入DOCTYPE申明后，js里是不能使用document.body的，一定要改成document.documentElement，还有就是加&quot;px&quot;等等。</p>', '&lt;!DOCTYPE html&gt;导致js问题', '9', '2015', 'Jan', '29', '1');
INSERT INTO `k_blogs` VALUES ('74', '2015-01-29 13:57:01', '<p>关于node.js与外接api的后端http响应交互，我组装了一个http接口插件</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n(function() {\r\n    function httpInterface(hostnameStr,portStr,pathStr,methodStr,dataStr,http_s,fn){\r\n        if(dataStr==null){\r\n            dataStr=&#39;plass callback&#39;;\r\n        }\r\n        if(http_s==0){\r\n            var httpOrHttps = require(&#39;http&#39;);\r\n        }else{\r\n            var httpOrHttps = require(&#39;https&#39;);\r\n        }\r\n        var options = {\r\n            hostname: hostnameStr,\r\n            port: portStr,\r\n            path: pathStr,\r\n            method: methodStr,\r\n            agent:false,\r\n            headers: {\r\n                &#39;Content-Type&#39;: &#39;application/x-www-form-urlencoded&#39;,\r\n                &#39;content-Length&#39;: dataStr.length\r\n            }\r\n        };\r\n        var body = &quot;&quot;;\r\n        var request = httpOrHttps.request(options,function(response){\r\n            response.setEncoding(&#39;utf8&#39;);\r\n            response.on(&#39;data&#39;,function(chunk){\r\n                body += chunk;\r\n            }).on(&#39;end&#39;,function(){\r\n                fn(undefined,body);\r\n            });\r\n        });\r\n        request.on(&#39;socket&#39;, function (e) {\r\n                e.setTimeout(5000);\r\n                e.on(&#39;timeout&#39;, function() {\r\n                    request.abort();\r\n                    var errorMessage = &quot;request timeout&quot; + e.message;\r\n                    fn(e,errorMessage);\r\n               });\r\n        });\r\n        request.on(&#39;error&#39;,function(e){\r\n            var errorMessage = &quot;Got error: &quot; + e.message;\r\n            fn(e,errorMessage);\r\n        });\r\n        request.write(dataStr+&quot;\\n&quot;)\r\n        request.end();\r\n    }\r\n    module.exports = httpInterface;\r\n}).call(this);</pre>\r\n\r\n<p>然后通过回调的方式引用：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nhttpInterface(opt.hostname,opt.port,opt.path,opt.method,opt.str,0,function(err,optData){\r\n     if(err){\r\n          optData= &#39;[]&#39;\r\n     }\r\n });</pre>\r\n\r\n<p>其中第6个参数：0代表http、1代表https</p>', 'http组件模型', '6', '2015', 'Jan', '29', '1');
INSERT INTO `k_blogs` VALUES ('75', '2015-02-05 14:35:58', '<p>超长文本传输协议（Hypertext Transfer Protocol，简称HTTP）是应用层协议。HTTP是一种请求/响应式的协议，即一个客户端与服务器建立连接后，向服务端发送一个请求；服务器接到请求后，给予相应的响应信息。</p>\r\n\r\n<h2>HTTP请求报文</h2>\r\n\r\n<p>HTTP请求报文由请求行、请求头部、空行和请求包体4个部分组成，</p>\r\n\r\n<p><img alt=\"HTTP图解\" src=\"http://y.photo.qq.com/img?s=mzn1W1GWA&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>下面对请求报文格式进行简单的分析：</p>\r\n\r\n<p>请求行：请求行由<span class=\"marker\">方法字段</span>、<span class=\"marker\">URL字段</span>和<span class=\"marker\">HTTP协议版本字段</span>3个部分组成，他们之间使用空格隔开。常用的HTTP请求方法有GET、POST、HEAD、PUT、DELETE、OPTIONS、TRACE、CONNECT；</p>\r\n\r\n<blockquote>\r\n<p><code>GET</code>：当客户端要从服务器中读取某个资源时，使用GET方法。GET方法要求服务器将URL定位的资源放在响应报文的数据部分，回送给客户端，即向服务器请求某个资源。使用GET方法时，请求参数和对应的值附加在URL后面，利用一个问号（&lsquo;？&rsquo;）代表URL的结尾与请求参数的开始，传递参数长度受限制。例如，&ldquo;/index.jsp?id=100&amp;op=bind&rdquo;。</p>\r\n\r\n<p><code>POST</code>：当客户端给服务器提供信息较多时可以使用POST方法，POST方法向服务器提交数据，比如完成表单数据的提交，将数据提交给服务器处理。GET一般用于获取/查询资源信息，POST会附带用户数据，一般用于更新资源信息。POST方法将请求参数封装在HTTP请求数据中，以名称/值的形式出现，可以传输大量数据。</p>\r\n</blockquote>\r\n\r\n<p>请求头部：请求头部由关键字/值对组成，每行一对，关键字和值用英文冒号&ldquo;:&rdquo;分隔。请求头部通知服务器有关于客户端请求的信息，典型的请求有；</p>\r\n\r\n<blockquote>\r\n<p><code>User-Agent</code>：产生请求的浏览器类型；</p>\r\n\r\n<p><code>Accept</code>：客户端可识别的响应内容类型列表；星号&ldquo;*&rdquo;用于按范围将类型分组，用&ldquo;*/*&rdquo;指示可接受type类型的所有子类型；</p>\r\n\r\n<p><code>Accept-Language</code>：客户端可接受的自然语言；</p>\r\n\r\n<p><code>Accept-Encoding</code>：客户端可接受的编码压缩格式；</p>\r\n\r\n<p><code>Accept-Charset</code>：可接受的应答的字符集；</p>\r\n\r\n<p><code>Host</code>：请求的主机名，允许多个域名同处一个IP地址，即虚拟主机；</p>\r\n\r\n<p><code>connection</code>：连接方式（close或keepalive）；</p>\r\n\r\n<p><code>Cookie</code>：存储于客户端扩展字段，向同一域名的服务端发送属于该域的cookie；</p>\r\n</blockquote>\r\n\r\n<p>空行 ：最后一个请求头之后是一个空行，发送回车符和换行符，通知服务器以下不再有请求头；</p>\r\n\r\n<p>请求包体：请求包体不在GET方法中使用，而是在POST方法中使用。POST方法适用于需要客户填写表单的场合。与请求包体相关的最常使用的是包体类型Content-Type和包体长度Content-Length;</p>\r\n\r\n<h2>HTTP响应报文</h2>\r\n\r\n<p>HTTP响应报文由状态行、响应头部、空行、响应包体4个部分组成，</p>\r\n\r\n<p><img alt=\"HTTP图解\" src=\"http://y.photo.qq.com/img?s=o7gUsPjoy&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>下面对响应报文格式进行简单的分析：</p>\r\n\r\n<p>状态行：状态行由HTTP协议版本字段、状态码和状态码的描述文本3个部分组成，他们之间使用空格隔开；</p>\r\n\r\n<blockquote>\r\n<p><code>HTTP协议版本字段</code>：HTTP的发展是万维网协会（World Wide Web Consortium）和Internet工作小组（Internet Engineering Task Force）合作的结果，（他们）最终发布的一系列的RFC；</p>\r\n\r\n<ul>\r\n	<li><kbd>RFC 1945</kbd>定义的HTTP/1.0版本；</li>\r\n	<li><kbd>RFC 2616</kbd>定义的HTTP/1.1版本；</li>\r\n</ul>\r\n\r\n<p><code>状态码</code>：由<em>http服务器对与请求HTTP响应状态的</em>三位数字组成，第一位数字表示响应的类型，它由 RFC 2616 规范定义的，并得到RFC 2518、RFC 2817、RFC 2295、RFC 2774、RFC 4918等规范扩展，<em>常用的状态码有五大类如下所示；</em></p>\r\n\r\n<ul>\r\n	<li><kbd>1xx</kbd>：表示服务器已接收了客户端请求，客户端可继续发送请求（请求已被接受，需要继续处理。这类响应是临时响应，只包含状态行和某些可选的响应头信息，并以空行结束。由于 HTTP/1.0 协议中没有定义任何 1xx 状态码，所以除非在某些试验条件下，服务器禁止向此类客户端发送 1xx 响应）；</li>\r\n	<li><kbd>2xx</kbd>：表示服务器已成功接收到请求并进行处理；</li>\r\n	<li><kbd>3xx</kbd>：表示服务器要求客户端重定向；</li>\r\n	<li><kbd>4xx</kbd>：表示客户端的请求有非法内容；</li>\r\n	<li><kbd>5xx</kbd>：表示服务器未能正常处理客户端的请求而出现意外错误；</li>\r\n</ul>\r\n\r\n<p><code>状态码的描述文本</code>：取值如下；</p>\r\n\r\n<ul>\r\n	<li><kbd>100 Continue</kbd>：客户端应当继续发送请求。这个临时响应是用来通知客户端它的部分请求已经被服务器接收，且仍未被拒绝。客户端应当继续发送请求的剩余部分，或者如果请求已经完成，忽略这个响应。服务器必须在请求完成后向客户端发送一个最终响应；</li>\r\n	<li><kbd>101&nbsp;Switching Protocols</kbd>：服务器已经理解了客户端的请求，并将通过Upgrade 消息头通知客户端采用不同的协议来完成这个请求。在发送完这个响应最后的空行后，服务器将会切换到在Upgrade 消息头中定义的那些协议。只有在切换新的协议更有好处的时候才应该采取类似措施。例如，切换到新的HTTP 版本比旧版本更有优势，或者切换到一个实时且同步的协议以传送利用此类特性的资源；</li>\r\n	<li><kbd>102&nbsp;Processing</kbd>：代表处理将被继续执行，由WebDAV（RFC 2518）扩展的状态码；</li>\r\n	<li><kbd>200 OK</kbd>：表示客户端请求成功<em>；</em></li>\r\n	<li><kbd><em>201&nbsp;</em>Created</kbd>：请求已经被实现，而且有一个新的资源已经依据请求的需要而建立，且其 URI 已经随Location 头信息返回。假如需要的资源无法及时建立的话，应当返回 &lsquo;202 Accepted&rsquo;；</li>\r\n	<li><kbd>202&nbsp;Accepted</kbd>：服务器已接受请求，但尚未处理。最终该请求可能会也可能不会被执行。在异步操作的场合下，没有比发送这个状态码更方便的做法了。返回202状态码的响应的目的是允许服务器接受其他过程的请求（例如某个每天只执行一次的基于批处理的操作），而不必让客户端一直保持与服务器的连接直到批处理操作全部完成。在接受请求处理并返回202状态码的响应应当在返回的实体中包含一些指示处理当前状态的信息，以及指向处理状态监视器或状态预测的指针，以便用户能够估计操作是否已经完成；</li>\r\n	<li><kbd>203&nbsp;Non-Authoritative Information</kbd>：服务器已成功处理了请求，但返回的实体头部元信息不是在原始服务器上有效的确定集合，而是来自本地或者第三方的拷贝。当前的信息可能是原始版本的子集或者超集。例如，包含资源的元数据可能导致原始服务器知道元信息的超级。使用此状态码不是必须的，而且只有在响应不使用此状态码便会返回200 OK的情况下才是合适的；</li>\r\n	<li><kbd>204&nbsp;No Content</kbd>：服务器成功处理了请求，但不需要返回任何实体内容，并且希望返回更新了的元信息。响应可能通过实体头部的形式，返回新的或更新后的元信息。如果存在这些头部信息，则应当与所请求的变量相呼应，如果客户端是浏览器的话，那么用户浏览器应保留发送了该请求的页面，而不产生任何文档视图上的变化，即使按照规范新的或更新后的元信息应当被应用到用户浏览器活动视图中的文档，由于204响应被禁止包含任何消息体，因此它始终以消息头后的第一个空行结尾；</li>\r\n	<li><kbd>205&nbsp;Reset Content</kbd>：服务器成功处理了请求，且没有返回任何内容。但是与204响应不同，返回此状态码的响应要求请求者重置文档视图。该响应主要是被用于接受用户输入后，立即重置表单，以便用户能够轻松地开始另一次输入，与204响应一样，该响应也被禁止包含任何消息体，且以消息头后的第一个空行结束；</li>\r\n	<li><kbd>206&nbsp;Partial Content</kbd>：服务器已经成功处理了部分 GET 请求。类似于 FlashGet 或者迅雷这类的 HTTP 下载工具都是使用此类响应实现断点续传或者将一个大文档分解为多个下载段同时下载，该请求必须包含 Range 头信息来指示客户端希望得到的内容范围，并且可能包含 If-Range 来作为请求条件；</li>\r\n	<li><kbd>207 Multi-Status</kbd>：由WebDAV（RFC 2518）扩展的状态码，代表之后的消息体将是一个XML消息，并且可能依照之前子请求数量的不同，包含一系列独立的响应代码；</li>\r\n	<li><kbd>300 Multiple Choices</kbd>：被请求的资源有一系列可供选择的回馈信息，每个都有自己特定的地址和浏览器驱动的商议信息。用户或浏览器能够自行选择一个首选的地址进行重定向，除非这是一个 HEAD 请求，否则该响应应当包括一个资源特性及地址的列表的实体，以便用户或浏览器从中选择最合适的重定向地址。这个实体的格式由Content-Type定义的格式所决定，浏览器可能根据响应的格式以及浏览器自身能力，自动作出最合适的选择，当然，RFC 2616规范并没有规定这样的自动选择该如何进行，如果服务器本身已经有了首选的回馈选择，那么在Location中应当指明这个回馈的URI；浏览器可能会将这个Location值作为自动重定向的地址。此外，除非额外指定，否则这个响应也是可缓存的；</li>\r\n	<li><kbd>301 Moved Permanently</kbd>：被请求的资源已永久移动到新位置，并且将来任何对此资源的引用都应该使用本响应返回的若干个 URI 之一。如果可能，拥有链接编辑功能的客户端应当自动把请求的地址修改为从服务器反馈回来的地址。除非额外指定，否则这个响应也是可缓存的。新的永久性的 URI 应当在响应的 Location 域中返回。除非这是一个 HEAD 请求，否则响应的实体中应当包含指向新的 URI 的超链接及简短说明。如果这不是一个 GET 或者 HEAD 请求，因此浏览器禁止自动进行重定向，除非得到用户的确认，因为请求的条件可能因此发生变化。注意：对于某些使用 HTTP/1.0 协议的浏览器，当它们发送的 POST 请求得到了一个301响应的话，接下来的重定向请求将会变成 GET 方式；</li>\r\n	<li><kbd>302 Found</kbd>：请求的资源现在临时从不同的 URI 响应请求。由于这样的重定向是临时的，客户端应当继续向原有地址发送以后的请求。只有在Cache-Control或Expires中进行了指定的情况下，这个响应才是可缓存的。新的临时性的 URI 应当在响应的 Location 域中返回。除非这是一个 HEAD 请求，否则响应的实体中应当包含指向新的 URI 的超链接及简短说明。如果这不是一个 GET 或者 HEAD 请求，那么浏览器禁止自动进行重定向，除非得到用户的确认，因为请求的条件可能因此发生变化。注意：虽然RFC 1945和RFC 2068规范不允许客户端在重定向时改变请求的方法，但是很多现存的浏览器将302响应视作为303响应，并且使用 GET 方式访问在 Location 中规定的 URI，而无视原先请求的方法。状态码303和307被添加了进来，用以明确服务器期待客户端进行何种反应；</li>\r\n	<li><kbd>303 See Other</kbd>：对应当前请求的响应可以在另一个 URI 上被找到，而且客户端应当采用 GET 的方式访问那个资源。这个方法的存在主要是为了允许由脚本激活的POST请求输出重定向到一个新的资源。这个新的 URI 不是原始资源的替代引用。同时，303响应禁止被缓存。当然，第二个请求（重定向）可能被缓存。新的 URI 应当在响应的 Location 域中返回。除非这是一个 HEAD 请求，否则响应的实体中应当包含指向新的 URI 的超链接及简短说明。注意：许多 HTTP/1.1 版以前的 浏览器不能正确理解303状态。如果需要考虑与这些浏览器之间的互动，302状态码应该可以胜任，因为大多数的浏览器处理302响应时的方式恰恰就是上述规范要求客户端处理303响应时应当做的；</li>\r\n	<li><kbd>304 Not Modified</kbd>：如果客户端发送了一个带条件的 GET 请求且该请求已被允许，而文档的内容（自上次访问以来或者根据请求的条件）并没有改变，则服务器应当返回这个状态码。304响应禁止包含消息体，因此始终以消息头后的第一个空行结尾。 该响应必须包含以下的头信息：Date，除非这个服务器没有时钟。假如没有时钟的服务器也遵守这些规则，那么代理服务器以及客户端可以自行将 Date 字段添加到接收到的响应头中去（正如RFC 2068中规定的一样），缓存机制将会正常工作。ETag 和/或 Content-Location，假如同样的请求本应返回200响应。Expires, Cache-Control，和/或Vary，假如其值可能与之前相同变量的其他响应对应的值不同的话。假如本响应请求使用了强缓存验证，那么本次响应不应该包含其他实体头；否则（例如，某个带条件的 GET 请求使用了弱缓存验证），本次响应禁止包含其他实体头；这避免了缓存了的实体内容和更新了的实体头信息之间的不一致。假如某个304响应指明了当前某个实体没有缓存，那么缓存系统必须忽视这个响应，并且重复发送不包含限制条件的请求。假如接收到一个要求更新某个缓存条目的304响应，那么缓存系统必须更新整个条目以反映所有在响应中被更新的字段的值；</li>\r\n	<li><kbd>305 Use Proxy</kbd>：被请求的资源必须通过指定的代理才能被访问。Location 域中将给出指定的代理所在的 URI 信息，接收者需要重复发送一个单独的请求，通过这个代理才能访问相应资源。只有原始服务器才能建立305响应，注意：RFC 2068中没有明确305响应是为了重定向一个单独的请求，而且只能被原始服务器建立。忽视这些限制可能导致严重的安全后果；</li>\r\n	<li><kbd>306 Switch Proxy</kbd>：在最新版的规范中，306状态码已经不再被使用；</li>\r\n	<li><kbd>307 Temporary Redirect</kbd>：请求的资源现在临时从不同的URI 响应请求。由于这样的重定向是临时的，客户端应当继续向原有地址发送以后的请求。只有在Cache-Control或Expires中进行了指定的情况下，这个响应才是可缓存的。新的临时性的URI 应当在响应的 Location 域中返回。除非这是一个HEAD 请求，否则响应的实体中应当包含指向新的URI 的超链接及简短说明。因为部分浏览器不能识别307响应，因此需要添加上述必要信息以便用户能够理解并向新的 URI 发出访问请求。如果这不是一个GET 或者 HEAD 请求，那么浏览器禁止自动进行重定向，除非得到用户的确认，因为请求的条件可能因此发生变化。 4xx 请求错误这类的状态码代表了客户端看起来可能发生了错误，妨碍了服务器的处理。除非响应的是一个 HEAD 请求，否则服务器就应该返回一个解释当前错误状况的实体，以及这是临时的还是永久性的状况。这些状态码适用于任何请求方法。浏览器应当向用户显示任何包含在此类错误响应中的实体内容。如果错误发生时客户端正在传送数据，那么使用TCP的服务器实现应当仔细确保在关闭客户端与服务器之间的连接之前，客户端已经收到了包含错误信息的数据包。如果客户端在收到错误信息后继续向服务器发送数据，服务器的TCP栈将向客户端发送一个重置数据包，以清除该客户端所有还未识别的输入缓冲，以免这些数据被服务器上的应用程序读取并干扰后者；</li>\r\n	<li><kbd>400 Bad Request</kbd>：表示客户端请求有语法错误，不能被服务器所理解，除非进行修改，否则客户端不应该重复提交这个请求；</li>\r\n	<li><kbd>401 Unauthorized</kbd>：表示请求未经授权，该状态代码必须与 WWW-Authenticate 报头域一起使用，该响应必须包含一个适用于被请求资源的WWW-Authenticate信息头用以询问用户信息。客户端可以重复提交一个包含恰当的 Authorization 头信息的请求。如果当前请求已经包含了Authorization证书，那么401响应代表着服务器验证已经拒绝了那些证书。如果401响应包含了与前一个响应相同的身份验证询问，且浏览器已经至少尝试了一次验证，那么浏览器应当向用户展示响应中包含的实体信息，因为这个实体信息中可能包含了相关诊断信息；</li>\r\n	<li><kbd>402 Payment Required</kbd>：该状态码是为了将来可能的需求而预留的；</li>\r\n	<li><kbd>403 Forbidden</kbd>：表示服务器收到请求，但是拒绝提供服务，通常会在响应正文中给出不提供服务的原因，与401响应不同的是，身份验证并不能提供任何帮助，而且这个请求也不应该被重复提交。如果这不是一个 HEAD请求，而且服务器希望能够讲清楚为何请求不能被执行，那么就应该在实体内描述拒绝的原因。当然服务器也可以返回一个404响应，假如它不希望让客户端获得任何信息；</li>\r\n	<li><kbd>404 Not Found</kbd>：请求的资源不存在，请求所希望得到的资源未被在服务器上发现。没有信息能够告诉用户这个状况到底是暂时的还是永久的。假如服务器知道情况的话，应当使用410状态码来告知旧资源因为某些内部的配置机制问题，已经永久的不可用，而且没有任何可以跳转的地址。404这个状态码被广泛应用于当服务器不想揭示到底为何请求被拒绝或者没有其他适合的响应可用的情况下；</li>\r\n	<li><kbd>405 Method Not Allowed</kbd>：请求行中指定的请求方法不能被用于请求相应的资源。该响应必须返回一个Allow 头信息用以表示出当前资源能够接受的请求方法的列表。鉴于 PUT，DELETE 方法会对服务器上的资源进行写操作，因而绝大部分的网页服务器都不支持或者在默认配置下不允许上述请求方法，对于此类请求均会返回405错误；</li>\r\n	<li><kbd>406 Not Acceptable</kbd>：请求的资源的内容特性无法满足请求头中的条件，因而无法生成响应实体。除非这是一个 HEAD 请求，否则该响应就应当返回一个包含可以让用户或者浏览器从中选择最合适的实体特性以及地址列表的实体。实体的格式由 Content-Type 头中定义的媒体类型决定。浏览器可以根据格式及自身能力自行作出最佳选择。但是，规范中并没有定义任何作出此类自动选择的标准；</li>\r\n	<li><kbd>407 Proxy Authentication Required</kbd>：与401响应类似，只不过客户端必须在代理服务器上进行身份验证。代理服务器必须返回一个 Proxy-Authenticate 用以进行身份询问。客户端可以返回一个 Proxy-Authorization 信息头用以验证。参见RFC 2617；</li>\r\n	<li><kbd>408 Request Timeout</kbd>：请求超时。客户端没有在服务器预备等待的时间内完成一个请求的发送。客户端可以随时再次提交这一请求而无需进行任何更改；</li>\r\n	<li><kbd>409 Conflict</kbd>：由于和被请求的资源的当前状态之间存在冲突，请求无法完成。这个代码只允许用在这样的情况下才能被使用：用户被认为能够解决冲突，并且会重新提交新的请求。该响应应当包含足够的信息以便用户发现冲突的源头。冲突通常发生于对 PUT 请求的处理中。例如，在采用版本检查的环境下，某次 PUT 提交的对特定资源的修改请求所附带的版本信息与之前的某个（第三方）请求向冲突，那么此时服务器就应该返回一个409错误，告知用户请求无法完成。此时，响应实体中很可能会包含两个冲突版本之间的差异比较，以便用户重新提交归并以后的新版本；</li>\r\n	<li><kbd>410 Gone</kbd>：被请求的资源在服务器上已经不再可用，而且没有任何已知的转发地址。这样的状况应当被认为是永久性的。如果可能，拥有链接编辑功能的客户端应当在获得用户许可后删除所有指向这个地址的引用。如果服务器不知道或者无法确定这个状况是否是永久的，那么就应该使用404状态码。除非额外说明，否则这个响应是可缓存的。410响应的目的主要是帮助网站管理员维护网站，通知用户该资源已经不再可用，并且服务器拥有者希望所有指向这个资源的远端连接也被删除。这类事件在限时、增值服务中很普遍。同样，410响应也被用于通知客户端在当前服务器站点上，原本属于某个个人的资源已经不再可用。当然，是否需要把所有永久不可用的资源标记为&rsquo;410 Gone&rsquo;，以及是否需要保持此标记多长时间，完全取决于服务器拥有者；</li>\r\n	<li><kbd>411 Length Required</kbd>：服务器拒绝在没有定义 Content-Length 头的情况下接受请求。在添加了表明请求消息体长度的有效 Content-Length 头之后，客户端可以再次提交该请求；</li>\r\n	<li><kbd>412 Precondition Failed</kbd>：服务器在验证在请求的头字段中给出先决条件时，没能满足其中的一个或多个。这个状态码允许客户端在获取资源时在请求的元信息（请求头字段数据）中设置先决条件，以此避免该请求方法被应用到其希望的内容以外的资源上；</li>\r\n	<li><kbd>413 Request Entity Too Large</kbd>：服务器拒绝处理当前请求，因为该请求提交的实体数据大小超过了服务器愿意或者能够处理的范围。此种情况下，服务器可以关闭连接以免客户端继续发送此请求。如果这个状况是临时的，服务器应当返回一个 Retry-After 的响应头，以告知客户端可以在多少时间以后重新尝试；</li>\r\n	<li><kbd>414 Request URI Too Long</kbd>：请求的URI 长度超过了服务器能够解释的长度，因此服务器拒绝对该请求提供服务。这比较少见，通常的情况包括： 本应使用POST方法的表单提交变成了GET方法，导致查询字符串（Query String）过长。 重定向URI &ldquo;黑洞&rdquo;，例如每次重定向把旧的 URI 作为新的 URI 的一部分，导致在若干次重定向后 URI 超长。 客户端正在尝试利用某些服务器中存在的安全漏洞攻击服务器。这类服务器使用固定长度的缓冲读取或操作请求的 URI，当 GET 后的参数超过某个数值后，可能会产生缓冲区溢出，导致任意代码被执行[1]。没有此类漏洞的服务器，应当返回414状态码；</li>\r\n	<li><kbd>415 Unsupported Media Type</kbd>：对于当前请求的方法和所请求的资源，请求中提交的实体并不是服务器中所支持的格式，因此请求被拒绝；</li>\r\n	<li><kbd>416 Requested Range Not Satisfiable</kbd>：如果请求中包含了 Range 请求头，并且 Range 中指定的任何数据范围都与当前资源的可用范围不重合，同时请求中又没有定义 If-Range 请求头，那么服务器就应当返回416状态码。假如 Range 使用的是字节范围，那么这种情况就是指请求指定的所有数据范围的首字节位置都超过了当前资源的长度。服务器也应当在返回416状态码的同时，包含一个 Content-Range 实体头，用以指明当前资源的长度。这个响应也被禁止使用 multipart/byteranges 作为其 Content-Type；</li>\r\n	<li><kbd>417 Expectation Failed</kbd>：在请求头 Expect 中指定的预期内容无法被服务器满足，或者这个服务器是一个代理服务器，它有明显的证据证明在当前路由的下一个节点上，Expect 的内容无法被满足；</li>\r\n	<li><kbd>418 I&rsquo;m a teapot</kbd>：1998年IETF的愚人节笑话, 在RFC2324 超文本咖啡壶控制协议中定义的，并不需要在真实的HTTP服务器中定义。 超文本咖啡壶控制协议：用于控制、监测和诊断咖啡壶的协议，愚人节恶搞之作；</li>\r\n	<li><kbd>421 There are too many connections from your internet address</kbd>：从当前客户端所在的IP地址到服务器的连接数超过了服务器许可的最大范围。通常，这里的IP地址指的是从服务器上看到的客户端地址（比如用户的网关或者代理服务器地址）。在这种情况下，连接数的计算可能涉及到不止一个终端用户；</li>\r\n	<li><kbd>422 Unprocessable Entity</kbd>：请求格式正确，但是由于含有语义错误，无法响应。（RFC 4918 WebDAV）423 Locked 　　当前资源被锁定；</li>\r\n	<li><kbd>424 Failed Dependency</kbd>：由于之前的某个请求发生的错误，导致当前请求失败，例如 PROPPATCH；</li>\r\n	<li><kbd>425 Unordered Collection</kbd>：在WebDav Advanced Collections 草案中定义，但是未出现在《WebDAV 顺序集协议》（RFC 3658）中；</li>\r\n	<li><kbd>426 Upgrade Required</kbd>：客户端应当切换到TLS/1.0；</li>\r\n	<li><kbd>449 Retry With</kbd>：由微软扩展，代表请求应当在执行完适当的操作后进行重试；</li>\r\n	<li><kbd>500 Internal Server Error</kbd>：表示服务器发生不可预期的错误，导致无法完成客户端的请求，这个问题都会在服务器的程序码出错时出现；</li>\r\n	<li><kbd>501 Not Implemented</kbd>：服务器不支持当前请求所需要的某个功能。当服务器无法识别请求的方法，并且无法支持其对任何资源的请求；</li>\r\n	<li><kbd>502 Bad Gateway</kbd>：作为网关或者代理工作的服务器尝试执行请求时，从上游服务器接收到无效的响应；</li>\r\n	<li><kbd>503 Service Unavailable</kbd>：表示服务器当前不能够处理客户端的请求，在一段时间之后，服务器可能会恢复正常，即由于临时的服务器维护或者过载，服务器当前无法处理请求。这个状况是临时的，并且将在一段时间以后恢复。如果能够预计延迟时间，那么响应中可以包含一个 Retry-After 头用以标明这个延迟时间。如果没有给出这个 Retry-After 信息，那么客户端应当以处理500响应的方式处理它。注意：503状态码的存在并不意味着服务器在过载的时候必须使用它。某些服务器只不过是希望拒绝客户端的连接；</li>\r\n	<li><kbd>504 Gateway Timeout</kbd>：作为网关或者代理工作的服务器尝试执行请求时，未能及时从上游服务器（URI标识出的服务器，例如HTTP、FTP、LDAP）或者辅助服务器（例如DNS）收到响应。注意：某些代理服务器在DNS查询超时时会返回400或者500错误；</li>\r\n	<li><kbd>505 HTTP Version Not Supported</kbd>：服务器不支持，或者拒绝支持在请求中使用的 HTTP 版本。这暗示着服务器不能或不愿使用与客户端相同的版本。响应中应当包含一个描述了为何版本不被支持以及服务器支持哪些协议的实体；</li>\r\n	<li><kbd>506 Variant Also Negotiates</kbd>：由《透明内容协商协议》（RFC 2295）扩展，代表服务器存在内部配置错误：被请求的协商变元资源被配置为在透明内容协商中使用自己，因此在一个协商处理中不是一个合适的重点；</li>\r\n	<li><kbd>507 Insufficient Storage</kbd>：服务器无法存储完成请求所必须的内容。这个状况被认为是临时的；</li>\r\n	<li><kbd>509 Bandwidth Limit Exceeded</kbd>：服务器达到带宽限制。这不是一个官方的状态码，但是仍被广泛使用；</li>\r\n	<li><kbd>510 Not Extended</kbd>：获取资源所需要的策略并没有没满足；</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<p>&nbsp;响应头部：响应的实体响应本身的HTML内容。头与实体之间由回车换行的空行(CRLF)分开；</p>\r\n\r\n<blockquote>\r\n<ul>\r\n	<li><kbd>Location</kbd>：Location响应报头域用于重定向接受者到一个新的位置。例如：客户端所请求的页面已不存在原先的位置，为了让客户端重定向到这个页面新的位置，服务器端可以发回Location响应报头后使用重定向语句，让客户端去访问新的域名所对应的服务器上的资源；</li>\r\n	<li><kbd>Server</kbd>：Server 响应报头域包含了服务器用来处理请求的软件信息及其版本。它和 User-Agent 请求报头域是相对应的，前者发送服务器端软件的信息，后者发送客户端软件(浏览器)和操作系统的信息；</li>\r\n	<li><kbd>Vary</kbd>：指示不可缓存的请求头列表；</li>\r\n	<li><kbd>Connection</kbd>：（连接方式）对于请求来说：close(告诉 WEB 服务器或者代理服务器，在完成本次请求的响应后，断开连接，不等待本次连接的后续请求了)。keepalive(告诉WEB服务器或者代理服务器，在完成本次请求的响应后，保持连接，等待本次连接的后续请求)；对于响应来说：close(连接已经关闭); keepalive(连接保持着，在等待本次连接的后续请求)；Keep-Alive：如果浏览器请求保持连接，则该头部表明希望WEB 服务器保持连接多长时间(秒)；例如：Keep-Alive：300；</li>\r\n	<li><kbd>WWW-Authenticate</kbd>：WWW-Authenticate响应报头域必须被包含在401 (未授权的)响应消息中，这个报头域和前面讲到的Authorization 请求报头域是相关的，当客户端收到 401 响应消息，就要决定是否请求服务器对其进行验证。如果要求服务器对其进行验证，就可以发送一个包含了Authorization 报头域的请求；</li>\r\n	<li><kbd>空行</kbd>：最后一个响应头部之后是一个空行，发送回车符和换行符，通知服务器以下不再有响应头部；</li>\r\n	<li><kbd>响应包体</kbd>：服务器返回给客户端的文本信息；</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<h2>HTTP工作原理&nbsp;</h2>\r\n\r\n<p>HTTP协议采用请求/响应模型。客户端向服务器发送一个请求报文，服务器以一个状态作为响应，HTTP请求/响应的步骤如下；</p>\r\n\r\n<blockquote>\r\n<ul>\r\n	<li><kbd>客户端连接到web服务器</kbd>：HTTP 客户端与web服务器建立一个 TCP 连接；</li>\r\n	<li><kbd>客户端向服务器发起 HTTP 请求</kbd>：通过已建立的TCP 连接，客户端向服务器发送一个请求报文；</li>\r\n	<li><kbd>服务器接收 HTTP 请求并返回 HTTP 响应</kbd>：服务器解析请求，定位请求资源，服务器将资源副本写到 TCP 连接，由客户端读取；</li>\r\n	<li><kbd>释放 TCP 连接</kbd>：若connection 模式为close，则服务器主动关闭TCP 连接，客户端被动关闭连接，释放TCP 连接;若connection 模式为keepalive，则该连接会保持一段时间，在该时间内可以继续接收请求；</li>\r\n	<li><kbd>客户端浏览器解析HTML内容</kbd>：客户端将服务器响应的 html 文本解析并显示；</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<h2>HTTP无状态性&nbsp;</h2>\r\n\r\n<p>HTTP协议是无状态的(stateless)。也就是说，同一个客户端第二次访问同一个服务器上的页面时，服务器无法知道这个客户端曾经访问过，服务器也无法分辨不同的客户端。HTTP 的无状态特性简化了服务器的设计，使服务器更容易支持大量并发的HTTP请求；</p>\r\n\r\n<h2>HTTP持久连接</h2>\r\n\r\n<p>HTTP1.0 使用的是非持久连接，主要缺点是客户端必须为每一个待请求的对象建立并维护一个新的连接，即每请求一个文档就要有两倍RTT 的开销。因为同一个页面可能存在多个对象，所以非持久连接可能使一个页面的下载变得十分缓慢，而且这种短连接增加了网络传输的负担。HTTP1.1 使用持久连接keepalive，所谓持久连接，就是服务器在发送响应后仍然在一段时间内保持这条连接，允许在同一个连接中存在多次数据请求和响应，即在持久连接情况下，服务器在发送完响应后并不关闭TCP 连接，而客户端可以通过这个连接继续请求其他对象；</p>\r\n\r\n<blockquote>\r\n<p>HTTP/1.1协议的持久连接有两种方式；</p>\r\n\r\n<ul>\r\n	<li><kbd>非流水线方式</kbd>：客户在收到前一个响应后才能发出下一个请求；</li>\r\n	<li><kbd>流水线方式</kbd>：客户在收到 HTTP 的响应报文之前就能接着发送新的请求报文；</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<p>粘一段GET示例代码：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nRemote Address:118.99.31.252:80\r\nRequest URL:http://www.sotrs.com/\r\nRequest Method:GET\r\nStatus Code:200 OK\r\nRequest Headers\r\n    Accept:text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8\r\n    Accept-Encoding:gzip,deflate,sdch\r\n    Accept-Language:zh-CN,zh;q=0.8\r\n    Cache-Control:max-age=0\r\n    Connection:keep-alive\r\n    Cookie:express:sess=eyJmbGFzaCI6e319; express:sess.sig=L4BJX57zQHywJKqs69vwmKktXLo; MyCssSkin=../css/skin/skin1.css\r\n    Host:www.sotrs.com\r\n    If-None-Match:W/&quot;wUz6bLD8l1cZ+JeyYZipTQ==&quot;\r\n    User-Agent:Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/31.0.1650.63 Safari/537.36\r\nResponse Headers\r\n    Connection:keep-alive\r\n	Date:Tue, 03 Feb 2015 15:09:19 GMT\r\n	ETag:W/&quot;wUz6bLD8l1cZ+JeyYZipTQ==&quot;\r\n	Server:nginx/1.6.2\r\n	X-Powered-By:Express\r\n</pre>\r\n\r\n<p>再粘一段POST示例代码：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nRequest URL:http://shiyeshu.com/toSourts\r\nRequest Method:POST\r\nStatus Code:200 OK\r\nRequest Headers\r\n    Accept:text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8\r\n	Accept-Encoding:gzip,deflate,sdch\r\n	Accept-Language:zh-CN,zh;q=0.8\r\n	Cache-Control:max-age=0\r\n	Connection:keep-alive\r\n	Content-Length:153\r\n	Content-Type:application/x-www-form-urlencoded\r\n	Cookie:MyCssSkin=../css/skin/skin1.css; express:sess=eyJmbGFzaCI6e30sInVzZXIiOiJrYW5namlhbiJ9; express:sess.sig=DZhDhz1-_fMTPY_yuj2FilWpLG8\r\n	Host:shiyeshu.com\r\n	Origin:http://shiyeshu.com\r\n	Referer:http://shiyeshu.com/editBlogById?blogs_ids=16\r\n	User-Agent:Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/31.0.1650.63 Safari/537.36\r\nForm Data\r\n	blogTitle:测试标题\r\n	blogId:16\r\n	blogTagId:9\r\n	blogYear:2011\r\n	blogYue:Jan\r\n	blogRi:1\r\n	editor1:测试\r\nResponse Headers\r\n	Connection:keep-alive\r\n	Content-Length:146\r\n	Content-Type:text/html; charset=utf-8\r\n	Date:Wed, 04 Feb 2015 03:06:38 GMT\r\n	Location:http://shiyeshu.com/editBlogById?blogs_ids=16\r\n	Server:nginx/1.6.2\r\n	Set-Cookie:express:sess=eyJmbGFzaCI6eyJzdWNjZXNzIjpbIua3u+WKoFwi5rWL6K+V5qCH6aKYXCLmlofnq6DmiJDlip8hIl19LCJ1c2VyIjoia2FuZ2ppYW4ifQ==; path=/; httponly\r\n	Set-Cookie:express:sess.sig=fBt0mTeuQJX6OzsevCRRl02TOks; path=/; httponly\r\n	Vary:Accept\r\n	X-Powered-By:Express\r\n</pre>', 'HTTP详解', '9', '2015', 'Feb', '2', '1');
INSERT INTO `k_blogs` VALUES ('76', '2015-02-05 14:48:43', '<pre class=\"linenums\" id=\"prettyprint\">\r\n<code>git clone url #克隆新的版本库</code>\r\n<code>git init</code>\r\n\r\n<code>git pull repo_name #有关联的远程库，抽取并和本地合并</code>\r\n\r\n<code>git fetch remote_repo_name #抽取并新建分支</code>\r\n\r\n\r\n<code> </code> \r\n\r\n\r\n<code>#在当前commit对象上新建分支   指针head</code>\r\n\r\n<code>#head指向正在工作中的本地分支的指针(别名)</code>\r\n\r\n<code>#不会切换到新建的分支上</code>\r\n\r\n<code>git branch branch1</code>\r\n\r\n\r\n<code>#切换分支将head指向branch1</code>\r\n\r\n<code>git checkout branch1</code>\r\n\r\n\r\n<code>#工作流程卡</code>\r\n\r\n<code>#在不同的分支里反复切换，并在时机成熟时把他们合并到一起</code>\r\n\r\n<code>#git的分支实际是一个包含所指向对象校验和的文件（</code><code>40</code><code>个字符长度SHA-</code><code>1</code><code>字串）</code>\r\n\r\n\r\n<code>#分支的新建和合并</code>\r\n\r\n<code>#</code><code>0</code><code>、自己工作分支mybranch，工作的好好的</code>\r\n\r\n\r\n<code>#</code><code>1</code><code>、突然有新需求，先切换到生产环境分支product；</code>\r\n\r\n<code>git checkout product</code>\r\n\r\n\r\n<code>#</code><code>2</code><code>、为新需求新建分支branch_pack，切到其中，并在其中编码，直到通过测试用例</code>\r\n\r\n<code># -b 新建分支并切换到其上</code>\r\n\r\n<code>git checkout -b issueXXXX</code>\r\n\r\n\r\n<code>#</code><code>3</code><code>、切换到生产环境分支product，将</code><code>2</code><code>中的开发工作分支branch_pack合并进来，然后推送到生产服务器上</code>\r\n\r\n<code>git checkout product</code>\r\n\r\n<code># 合并</code>\r\n\r\n<code>git merge branch_pach</code>\r\n\r\n<code>#冲突解决，merge失败时很可能因对同一文件的同时更改，所以必须手动人工解决 取舍代码，确认冲突解决后需要把冲突文件 git add到staged area，运行git status查看所有状态确保无误后再git commit提交，注释好冲突为什么这么解决</code>\r\n\r\n\r\n<code>git push 远程仓库名  本地仓库名</code>\r\n\r\n\r\n<code>#分支删除</code>\r\n\r\n<code>git branch -d branch_pach</code>\r\n\r\n\r\n<code>4</code><code>、切换到mybranch继续自己的工作</code>\r\n\r\n<code>git checkout mybranch</code>\r\n\r\n<code> </code> \r\n\r\n<code>#切换分支最好没有待提交的文件，即stage area是清洁的</code>\r\n\r\n<code>#查看各个分支最后一个提交对象的信息</code>\r\n\r\n<code>git branch -v</code>\r\n\r\n<code>git branch --merge/no-merged</code>\r\n\r\n<code>#查看哪些分支已被并入当前分支（译注：也就是说哪些分支是当前分支的直接上游</code>\r\n\r\n\r\n\r\n\r\n<code>#给分支生成patch文件可以给</code>\r\n\r\n<code>git format-patch</code>\r\n\r\n\r\n\r\n<code>#把本地名为serverfix的分支推送到远程origin库的awesomebranch分支</code>\r\n\r\n<code>git push origin serverfix:awesomebranch</code>\r\n\r\n\r\n<code>#跟踪远程分支 从远程分支checkout出的本地分支成为  跟踪分支</code>\r\n\r\n<code>git checkout -b sf origin/serverfix</code>\r\n\r\n<code>git checkout --track origin/serverfix</code>\r\n\r\n<code> </code> \r\n\r\n<code>#删除远程分支</code>\r\n\r\n<code>git push origin :serverfix</code>\r\n\r\n\r\n<code>git两种开发分支方法</code>\r\n\r\n<code>长期分支：一个主分支保持稳定代码，其余多个开放分支</code>\r\n\r\n<code>特性topic分支：多个短期单一功能的分支</code>\r\n\r\n\r\n<code>#给当前文件 打特定版本标签</code>\r\n\r\n<code>git tag -a  beta0.</code><code>1</code> <code>-m </code><code>&quot;some 注释&quot;</code>\r\n\r\n\r\n<code>#回退到某个tag</code>\r\n\r\n<code>git tag beta0.</code><code>1</code></pre>', 'git新说', '5', '2015', 'Feb', '5', '1');
INSERT INTO `k_blogs` VALUES ('77', '2015-02-06 13:05:09', '<p>测试一下</p>', '测试标题', '24', '2011', 'Jan', '1', '1');
INSERT INTO `k_blogs` VALUES ('78', '2015-02-10 23:38:18', '<p>现在的主流浏览器有五种，即IE、Firefox、Safari、Chrome及Opera，其中Firefox、 Chrome开源，Safari部分开源。</p>\r\n\r\n<h3>浏览器的主要构成：</h3>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>用户界面 － 包括地址栏、后退/前进按钮、书签目录等，也就是你所看到的除了用来显示你所请求页面的主窗口之外的其他部分。</li>\r\n	<li>浏览器引擎 － 用来查询及操作渲染引擎的接口。</li>\r\n	<li>渲染引擎 － 用来显示请求的内容，例如，如果请求内容为html，它负责解析html及css，并将解析后的结果显示出来。</li>\r\n	<li>网络 － 用来完成网络调用，例如http请求，它具有平台无关的接口，可以在不同平台上工作。</li>\r\n	<li>UI后端 － 用来绘制类似组合选择框及对话框等基本组件，具有不特定于某个平台的通用接口，底层使用操作系统的用户接口。</li>\r\n	<li>JS解释器 － 用来解释执行JS代码。</li>\r\n	<li>数据存储 － 属于持久层，浏览器需要在硬盘中保存类似cookie的各种数据，HTML5定义了web database技术，这是一种轻量级完整的客户端存储技术。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>*（不同于大部分浏览器，Chrome为每个Tab分配了各自的渲染引擎实例，每个Tab就是一个独立的进程。）</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=D1BCcO8jo&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h1>一、渲染引擎（The rendering engine）</h1>\r\n\r\n<p>渲染引擎的职责就是渲染，即在浏览器窗口中显示所请求的内容，默认情况下，渲染引擎可以显示html、xml文档及图片，它也可以借助浏览器插件显示其他类型数据，（如PDF阅读器插件），Firefox、Chrome和Safari是基于两种渲染引擎构建的，Firefox使用Geoko（Mozilla自主研发的渲染引擎），Safari和Chrome都使用webkit（Webkit是一款开源渲染引擎，它本来是为Linux平台研发的，后来由Apple移植到Mac、Windows）；</p>\r\n\r\n<h2>渲染主流程（The main flow）</h2>\r\n\r\n<p>渲染引擎首先通过网络获得所请求文档的内容（通常以8K分块的方式完成），渲染引擎在取得内容之后的流程如下：</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=SlhREK9dM&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>渲染引擎基本流程：解析html以构建dom树 -&gt; 构建render树 -&gt; 布局render树 -&gt; 绘制render树</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>渲染引擎开始解析html，并将标签转化为内容树中的dom节点。接着，它解析外部CSS文件及style标签中的样式信息。这些样式信息以及html中的可见性指令将被用来构建另一棵树&mdash;&mdash;render树；</li>\r\n	<li>Render树由一些包含有颜色和大小等属性的矩形组成，它们将被按照正确的顺序显示到屏幕上；</li>\r\n	<li>Render树构建好了之后，将会执行布局过程，它将确定每个节点在屏幕上的确切坐标。再下一步就是绘制，即遍历render树，并使用UI后端层绘制每个节点；</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>*（整个过程都是逐步完成，为了更好的用户体验，渲染引擎将会尽可能早的将内容呈现到屏幕上，并不会等到所有的html都解析完成之后再去构建和布局render树。它是解析完一部分内容就显示一部分内容，同时，可能还在通过网络下载其余内容）&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=a6TVSsMv6&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=2nFPNejow&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>从图中可以看出尽管webkit和Gecko使用的术语稍有不同，但主要流程基本相同；</p>\r\n\r\n<p>Gecko称可见的格式化元素组成的树为frame树，每个元素都是一个frame，webkit则使用render树这个名词来命名由渲染对象组成的树。Webkit中元素的定位称为布局，而Gecko中称为回流。Webkit称利用dom节点及样式信息去构建render树的过程为attachment，Gecko在html和dom树之间附加了一层，这层称为内容接收器，相当制造dom元素的工厂。</p>\r\n\r\n<h1>二、解析与DOM树构建（Parsing and DOM tree construction）</h1>\r\n\r\n<h2>解析（Parsing－general）</h2>\r\n\r\n<p>解析是渲染引擎中一个非常重要的过程，解析一个文档即将其转换为具有一定意义的结构&mdash;&mdash;编码可以理解和使用的东西。解析的结果通常是表达文档结构的节点树，称为解析树或语法树；</p>\r\n\r\n<h2>文法（Grammars）</h2>\r\n\r\n<p>解析基于文档依据的语法规则&mdash;&mdash;文档的语言或格式。每种可被解析的格式必须具有由词汇及语法规则组成的特定的文法，称为上下文无关文法。人类语言不具有这一特性，因此不能被一般的解析技术所解析。</p>\r\n\r\n<h2>解析器－词法分析器（Parser－Lexer combination）</h2>\r\n\r\n<p>解析分为两个子过程，即<code>语法分析</code>和<code>词法分析</code></p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>　　词法分析就是将输入分解为符号，符号是语言的词汇表&mdash;&mdash;基本有效单元的集合。对于人类语言来说，它相当于我们字典中出现的所有单词。</li>\r\n	<li>　　语法分析指对语言应用语法规则。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>解析器一般将工作分配给两个组件</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>词法分析器（有时也叫分词器）负责将输入分解为合法的符号；</li>\r\n	<li>解析器则根据语言的语法规则分析文档结构，从而构建解析树，词法分析器知道怎么跳过空白和换行之类的无关字符。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"http://y.photo.qq.com/img?s=3y5JA1XmC&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>解析过程是迭代的，解析器从词法分析器处取到一个新的符号，并试着用这个符号匹配一条语法规则，如果匹配了一条规则，这个符号对应的节点将被添加到解析树上，然后解析器请求另一个符号。如果没有匹配到规则，解析器将在内部保存该符号，并从词法分析器取下一个符号，直到所有内部保存的符号能够匹配一项语法规则。如果最终没有找到匹配的规则，解析器将抛出一个异常，这意味着文档无效或是包含语法错误。</p>\r\n\r\n<h2>转换（Translation）</h2>\r\n\r\n<p>很多时候，解析树并不是最终结果。解析一般在转换中使用&mdash;&mdash;将输入文档转换为另一种格式。编译就是个例子，编译器在将一段源码编译为机器码的时候，先将源码解析为解析树，然后将该树转换为一个机器码文档。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=4DqS3FGFC&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h2>HTML解析器（HTML Parser）</h2>\r\n\r\n<p>HTML解析器的工作是将html标识解析为解析树。</p>\r\n\r\n<h2>HTML文法定义（The HTML grammar definition）</h2>\r\n\r\n<p>W3C组织制定规范定义了HTML的词汇表和语法。</p>\r\n\r\n<h2>HTML DTD</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>Html适用DTD格式进行定义，这一格式是用于定义SGML家族的语言，包括了对所有允许元素及它们的属性和层次关系的定义。正如前面提到的，html DTD并没有生成一种上下文无关文法；DTD有一些变种，标准模式只遵守规范，而其他模式则包含了对浏览器过去所使用标签的支持，这么做是为了兼容以前内容。</p>\r\n\r\n<h2>DOM</h2>\r\n\r\n<p>输出的树，也就是解析树（由DOM元素及属性节点组成）。DOM是文档对象模型的缩写，它是html文档的对象表示，作为html元素的外部接口供js等调用；树的根是&ldquo;document&rdquo;对象。</p>\r\n\r\n<p>DOM和标签基本是一一对应的关系，如：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;html&gt;\r\n    &lt;body&gt;\r\n        &lt;p&gt;\r\n            Hello DOM\r\n        &lt;/p&gt;\r\n        &lt;div&gt;\r\n            &lt;img src=&rdquo;hello.png&rdquo; /&gt;\r\n        &lt;/div&gt;\r\n    &lt;/body&gt;\r\n&lt;/html&gt;</pre>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=OzPJ9S3QV&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>*（树包含了DOM节点是说树是由实现了DOM接口的元素构建而成的，浏览器使用已被浏览器内部使用的其他属性的具体实现）</p>\r\n\r\n<h2>解析算法（The parsing algorithm）</h2>\r\n\r\n<p>hmtl不能被一般的自顶向下或自底向上的解析器所解析的原因如下：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>这门语言本身的宽容特性</li>\r\n	<li>浏览器对一些常见的非法html有容错机制</li>\r\n	<li>解析过程是往复的，通常源码不会在解析过程中发生改变，但在html中，脚本标签包含的&ldquo;document.write&rdquo;可能添加标签，这说明在解析过程中实际上修改了输入。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>不能使用正则解析技术，浏览器为html定制了专属的解析器。</p>\r\n\r\n<p>Html5规范中描述了这个解析算法，算法包括两个阶段&mdash;&mdash;符号化及构建树；符号化是词法分析的过程，将输入解析为符号，html的符号包括开始标签、结束标签、属性名及属性值；符号识别器识别出符号后，将其传递给树构建器，并读取下一个字符，以识别下一个符号，这样直到处理完所有输入。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=HuNlwvAqe&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h2>符号识别算法（The tokenization algorithm）</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>算法输出html符号，该算法用状态机表示。每次读取输入流中的一个或多个字符，并根据这些字符转移到下一个状态，当前的符号状态及构建树状态共同影响结果，这意味着，读取同样的字符，可能因为当前状态的不同，得到不同的结果以进入下一个正确的状态。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=kpvtIjuZB&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h2>树的构建算法（Tree construction algorithm）</h2>\r\n\r\n<p>在树的构建阶段，将修改以Document为根的DOM树，将元素附加到树上。每个由符号识别器识别生成的节点将会被树构造器进行处理，规范中定义了每个符号相对应的Dom元素，对应的Dom元素将会被创建。这些元素除了会被添加到Dom树上，还将被添加到开放元素堆栈中。这个堆栈用来纠正嵌套的未匹配和未闭合标签，这个算法也是用状态机来描述，所有的状态采用插入模式。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;html&gt;\r\n    &lt;body&gt;\r\n        Hello world\r\n    &lt;/body&gt;\r\n&lt;/html&gt;</pre>\r\n\r\n<p>构建树这一阶段的输入是符号识别阶段生成的符号序列；首先是&ldquo;initial mode&rdquo;，接收到html符号后将转换为&ldquo;before html&rdquo;模式，在这个模式中对这个符号进行再处理。此时，创建了一个HTMLHtmlElement元素，并将其附加到根Document对象上；状态此时变为&ldquo;before head&rdquo;，接收到body符号时，即使这里没有head符号，也将自动创建一个HTMLHeadElement元素并附加到树上；现在，转到&ldquo;in head&rdquo;模式，然后是&ldquo;after head&rdquo;。到这里，body符号会被再次处理，将创建一个HTMLBodyElement并插入到树中，同时，转移到&ldquo;in body&rdquo;模式；然后，接收到字符串&ldquo;Hello world&rdquo;的字符符号，第一个字符将导致创建并插入一个text节点，其他字符将附加到该节点；接收到body结束符号时，转移到&ldquo;after body&rdquo;模式，接着接收到html结束符号，这个符号意味着转移到了&ldquo;after after body&rdquo;模式，当接收到文件结束符时，整个解析过程结束。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=bBPYLIGv3&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>解析结束时的处理（Action when the parsing is finished）</h2>\r\n\r\n<p>在这个阶段，浏览器将文档标记为可交互的，并开始解析处于延时模式中的脚本&mdash;&mdash;这些脚本在文档解析后执行。</p>\r\n\r\n<p>文档状态将被设置为完成，同时触发一个load事件。</p>\r\n\r\n<p>Html5规范中有符号化及构建树的完整算法(http://www.w3.org/TR/html5/syntax.html#html-parser)。</p>\r\n\r\n<h2>浏览器容错（Browsers error tolerance）</h2>\r\n\r\n<p>你从来不会在一个html页面上看到&ldquo;无效语法&rdquo;这样的错误，浏览器修复了无效内容并继续工作。以下面这段html为例：</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;html&gt;\r\n    &lt;mytag&gt;\r\n    &lt;/mytag&gt;\r\n    &lt;div&gt;\r\n        &lt;p&gt;\r\n        &lt;/div&gt;\r\n        Really lousy HTML\r\n    &lt;/p&gt;\r\n&lt;/html&gt;</pre>\r\n\r\n<p>这段html违反了很多规则（mytag不是合法的标签，p及div错误的嵌套等等），但是浏览器仍然可以没有任何怨言的继续显示，它在解析的过程中修复了html作者的错误。</p>\r\n\r\n<p>浏览器都具有错误处理的能力，但是，另人惊讶的是，这并不是html最新规范的内容，就像书签及前进后退按钮一样，它只是浏览器长期发展的结果。一些比较知名的非法html结构，在许多站点中出现过，浏览器都试着以一种和其他浏览器一致的方式去修复。</p>\r\n\r\n<p>Html5规范定义了这方面的需求，webkit在html解析类开始部分的注释中做了很好的总结。</p>\r\n\r\n<p>解析器将符号化的输入解析为文档并创建文档，但不幸的是，我们必须处理很多没有很好格式化的html文档，至少要小心下面几种错误情况。</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>在未闭合的标签中添加明确禁止的元素。这种情况下，应该先将前一标签闭合</li>\r\n	<li>不能直接添加元素。有些人在写文档的时候会忘了中间一些标签（或者中间标签是可选的），比如HTML HEAD BODY TR TD LI等</li>\r\n	<li>想在一个行内元素中添加块状元素。关闭所有的行内元素，直到下一个更高的块状元素</li>\r\n	<li>如果这些都不行，就闭合当前标签直到可以添加该元素。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>下面来看一些webkit容错的例子：</p>\r\n\r\n<p>1、&lt;/br&gt;替代&lt;br&gt;</p>\r\n\r\n<p>为了兼容IE和Firefox，webkit将其看作&lt;br&gt;。</p>\r\n\r\n<p>2、迷路的表格</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;table&gt;\r\n    &lt;table&gt;\r\n        &lt;tr&gt;&lt;td&gt;inner table&lt;/td&gt;&lt;/tr&gt;\r\n    &lt;/table&gt;\r\n    &lt;tr&gt;&lt;td&gt;outer table&lt;/td&gt;&lt;/tr&gt;\r\n&lt;/table&gt;</pre>\r\n\r\n<p>webkit将会将嵌套的表格变为两个兄弟表格：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;table&gt;\r\n    &lt;tr&gt;&lt;td&gt;outer table&lt;/td&gt;&lt;/tr&gt;\r\n&lt;/table&gt;\r\n&lt;table&gt;\r\n    &lt;tr&gt;&lt;td&gt;inner table&lt;/td&gt;&lt;/tr&gt;\r\n&lt;/table&gt;</pre>\r\n\r\n<p>webkit使用堆栈存放当前的元素内容，它将从外部表格的堆栈中弹出内部的表格，则它们变为了兄弟表格。</p>\r\n\r\n<p>3、嵌套的表单元素</p>\r\n\r\n<p>用户将一个表单嵌套到另一个表单中，则第二个表单将被忽略；</p>\r\n\r\n<p>4、太深的标签继承</p>\r\n\r\n<p>最多只允许20个相同类型的标签嵌套，多出来的将被忽略。</p>\r\n\r\n<h2>CSS解析（CSS parsing）</h2>\r\n\r\n<p>还记得简介中提到的解析的概念吗，不同于html，css属于上下文无关文法，可以用前面所描述的解析器来解析。Css规范定义了css的词法及语法文法。</p>\r\n\r\n<h2>Webkit CSS解析器（Webkit CSS parser）</h2>\r\n\r\n<p>Webkit使用Flex和Bison解析生成器从CSS语法文件中自动生成解析器。回忆一下解析器的介绍，Bison创建一个自底向上的解析器，Firefox使用自顶向下解析器。它们都是将每个css文件解析为样式表对象，每个对象包含css规则，css规则对象包含选择器和声明对象，以及其他一些符合css语法的对象。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=1v6d8SDH8&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h1>三、处理脚本及样式表的顺序（The order of processing scripts and style sheets）</h1>\r\n\r\n<h2>脚本</h2>\r\n\r\n<p>web的模式是同步的，开发者希望解析到一个script标签时立即解析执行脚本，并阻塞文档的解析直到脚本执行完。如果脚本是外引的，则网络必须先请求到这个资源&mdash;&mdash;这个过程也是同步的，会阻塞文档的解析直到资源被请求到。这个模式保持了很多年，并且在html4及html5中都特别指定了。开发者可以将脚本标识为defer，以使其不阻塞文档解析，并在文档解析结束后执行。Html5增加了标记脚本为异步的选项，以使脚本的解析执行使用另一个线程。</p>\r\n\r\n<h2>预解析（Speculative parsing）</h2>\r\n\r\n<p>Webkit和Firefox都做了这个优化，当执行脚本时，另一个线程解析剩下的文档，并加载后面需要通过网络加载的资源。这种方式可以使资源并行加载从而使整体速度更快。需要注意的是，预解析并不改变Dom树，它将这个工作留给主解析过程，自己只解析外部资源的引用，比如外部脚本、样式表及图片。</p>\r\n\r\n<h2>样式表（Style sheets）</h2>\r\n\r\n<p>样式表采用另一种不同的模式。理论上，既然样式表不改变Dom树，也就没有必要停下文档的解析等待它们，然而，存在一个问题，脚本可能在文档的解析过程中请求样式信息，如果样式还没有加载和解析，脚本将得到错误的值，显然这将会导致很多问题，这看起来是个边缘情况，但确实很常见。Firefox在存在样式表还在加载和解析时阻塞所有的脚本，而Chrome只在当脚本试图访问某些可能被未加载的样式表所影响的特定的样式属性时才阻塞这些脚本。</p>\r\n\r\n<h1>四、渲染树构建（Render tree construction）</h1>\r\n\r\n<p>当Dom树构建完成时，浏览器开始构建另一棵树&mdash;&mdash;渲染树。渲染树由元素显示序列中的可见元素组成，它是文档的可视化表示，构建这棵树是为了以正确的顺序绘制文档内容。</p>\r\n\r\n<p>Firefox将渲染树中的元素称为frames，WebKit则用renderer或渲染对象来描述这些元素。</p>\r\n\r\n<p>一个渲染对象知道怎么布局及绘制自己及它的children。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>RenderObject是Webkit的渲染对象基类，它的定义如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nclass RenderObject{\r\n    virtual void layout();\r\n    virtual void paint(PaintInfo);\r\n    virtual void rect repaintRect();\r\n    Node* node;//the DOM node\r\n    RenderStyle* style;// the computed style\r\n    RenderLayer* containgLayer; //the containing z-index layer\r\n}</pre>\r\n\r\n<p>每个渲染对象用一个和该节点的css盒模型相对应的矩形区域来表示，正如css2所描述的那样，它包含诸如宽、高和位置之类的几何信息。盒模型的类型受该节点相关的display样式属性的影响（参考样式计算章节）。下面的webkit代码说明了如何根据display属性决定某个节点创建何种类型的渲染对象。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nRenderObject* RenderObject::createObject(Node* node, RenderStyle* style)\r\n{\r\n    Document* doc = node-&gt;document();\r\n    RenderArena* arena = doc-&gt;renderArena();\r\n    ...\r\n    RenderObject* o = 0;\r\n    switch (style-&gt;display()) {\r\n        case NONE:\r\n        break;\r\n        case INLINE:\r\n        o = new (arena) RenderInline(node);\r\n        break;\r\n        case BLOCK:\r\n        o = new (arena) RenderBlock(node);\r\n        break;\r\n        case INLINE_BLOCK:\r\n        o = new (arena) RenderBlock(node);\r\n        break;\r\n        case LIST_ITEM:\r\n        o = new (arena) RenderListItem(node);\r\n        break;\r\n        ...\r\n    }\r\n    return o;\r\n}</pre>\r\n\r\n<p>元素的类型也需要考虑，例如，表单控件和表格带有特殊的框架。</p>\r\n\r\n<p>在Webkit中，如果一个元素想创建一个特殊的渲染对象，它需要重写&ldquo;createRenderer&rdquo;方法，使渲染对象指向不包含几何信息的样式对象。</p>\r\n\r\n<h2>渲染树和Dom树的关系（The render tree relation to the DOM tree）</h2>\r\n\r\n<p>渲染对象和Dom元素相对应，但这种对应关系不是一对一的，不可见的Dom元素不会被插入渲染树，例如head元素。另外，display属性为none的元素也不会在渲染树中出现（visibility属性为hidden的元素将出现在渲染树中）。<br />\r\n还有一些Dom元素对应几个可见对象，它们一般是一些具有复杂结构的元素，无法用一个矩形来描述。例如，select元素有三个渲染对象&mdash;&mdash;一个显示区域、一个下拉列表及一个按钮。同样，当文本因为宽度不够而折行时，新行将作为额外的渲染元素被添加。另一个多个渲染对象的例子是不规范的html，根据css规范，一个行内元素只能仅包含行内元素或仅包含块状元素，在存在混合内容时，将会创建匿名的块状渲染对象包裹住行内元素。<br />\r\n一些渲染对象和所对应的Dom节点不在树上相同的位置，例如，浮动和绝对定位的元素在文本流之外，在两棵树上的位置不同，渲染树上标识出真实的结构，并用一个占位结构标识出它们原来的位置。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=P1NcNCOE3&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h2>创建树的流程（The flow of constructing the tree）</h2>\r\n\r\n<p>Firefox中，表述为一个监听Dom更新的监听器，将frame的创建委派给Frame Constructor，这个构建器计算样式（参看样式计算）并创建一个frame。<br />\r\nWebkit中，计算样式并生成渲染对象的过程称为attachment，每个Dom节点有一个attach方法，attachment的过程是同步的，调用新节点的attach方法将节点插入到Dom树中。<br />\r\n处理html和body标签将构建渲染树的根，这个根渲染对象对应被css规范称为containing block的元素&mdash;&mdash;包含了其他所有块元素的顶级块元素。它的大小就是viewport&mdash;&mdash;浏览器窗口的显示区域，Firefox称它为viewPortFrame，webkit称为RenderView，这个就是文档所指向的渲染对象，树中其他的部分都将作为一个插入的Dom节点被创建。</p>\r\n\r\n<h2>样式计算（Style Computation）</h2>\r\n\r\n<p>创建渲染树需要计算出每个渲染对象的可视属性，这可以通过计算每个元素的样式属性得到。<br />\r\n样式包括各种来源的样式表，行内样式元素及html中的可视化属性（例如bgcolor），可视化属性转化为css样式属性。<br />\r\n样式表来源于浏览器默认样式表，及页面作者和用户提供的样式表&mdash;&mdash;有些样式是浏览器用户提供的（浏览器允许用户定义喜欢的样式，例如，在Firefox中，可以通过在Firefox Profile目录下放置样式表实现）。<br />\r\n计算样式的一些困难：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>样式数据是非常大的结构，保存大量的样式属性会带来内存问题。</li>\r\n	<li>如果不进行优化，找到每个元素匹配的规则会导致性能问题，为每个元素查找匹配的规则都需要遍历整个规则表，这个过程有很大的工作量。选择符可能有复杂的结构，匹配过程如果沿着一条开始看似正确，后来却被证明是无用的路径，则必须去尝试另一条路径。</li>\r\n	<li>应用规则涉及非常复杂的级联，它们定义了规则的层次。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<h2>共享样式数据（Sharing style data）</h2>\r\n\r\n<p>WebkKit节点引用样式对象（渲染样式），某些情况下，这些对象可以被节点间共享，这些节点需要是兄弟或是表兄弟节点，并且：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>这些元素必须处于相同的鼠标状态（比如不能一个处于hover，而另一个不是）</li>\r\n	<li>不能有元素具有id</li>\r\n	<li>标签名必须匹配</li>\r\n	<li>class属性必须匹配</li>\r\n	<li>对应的属性必须相同</li>\r\n	<li>链接状态必须匹配</li>\r\n	<li>焦点状态必须匹配</li>\r\n	<li>不能有元素被属性选择器影响</li>\r\n	<li>元素不能有行内样式属性</li>\r\n	<li>不能有生效的兄弟选择器，webcore在任何兄弟选择器相遇时只是简单的抛出一个全局转换，并且在它们显示时使整个文档的样式共享失效，这些包括＋选择器和类似:first-child和:last-child这样的选择器。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Firefox规则树（Firefox rule tree）</h2>\r\n\r\n<p>Firefox用两个树用来简化样式计算－规则树和样式上下文树，WebKit也有样式对象，但它们并没有存储在类似样式上下文树这样的树中，只是由Dom节点指向其相关的样式。</p>\r\n\r\n<p>样式上下文包含最终值，这些值是通过以正确顺序应用所有匹配的规则，并将它们由逻辑值转换为具体的值，例如，如果逻辑值为屏幕的百分比，则通过计算将其转化为绝对单位。样式树的使用确实很巧妙，它使得在节点中共享的这些值不需要被多次计算，同时也节省了存储空间。</p>\r\n\r\n<p>所有匹配的规则都存储在规则树中，一条路径中的底层节点拥有最高的优先级，这棵树包含了所找到的所有规则匹配的路径（译注：可以取巧理解为每条路径对应一个节点，路径上包含了该节点所匹配的所有规则）。规则树并不是一开始就为所有节点进行计算，而是在某个节点需要计算样式时，才进行相应的计算并将计算后的路径添加到树中。</p>\r\n\r\n<h2>结构化</h2>\r\n\r\n<p>样式上下文按结构划分，这些结构包括类似border或color这样的特定分类的样式信息。一个结构中的所有特性不是继承的就是非继承的，对继承的特性，除非元素自身有定义，否则就从它的parent继承。非继承的特性（称为reset特性）如果没有定义，则使用默认的值。<br />\r\n样式上下文树缓存完整的结构（包括计算后的值），这样，如果底层节点没有为一个结构提供定义，则使用上层节点缓存的结构。</p>\r\n\r\n<h2>使用规则树计算样式上下文</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当为一个特定的元素计算样式时，首先计算出规则树中的一条路径，或是使用已经存在的一条，然后使用路径中的规则去填充新的样式上下文，从样式的底层节点开始，它具有最高优先级（通常是最特定的选择器），遍历规则树，直到填满结构。如果在那个规则节点没有定义所需的结构规则，则沿着路径向上，直到找到该结构规则。<br />\r\n如果最终没有找到该结构的任何规则定义，那么如果这个结构是继承型的，则找到其在内容树中的parent的结构，这种情况下，我们也成功的共享了结构；如果这个结构是reset型的，则使用默认的值。<br />\r\n如果特定的节点添加了值，那么需要做一些额外的计算以将其转换为实际值，然后在树上的节点缓存该值，使它的children可以使用。<br />\r\n当一个元素和它的一个兄弟元素指向同一个树节点时，完整的样式上下文可以被它们共享。<br />\r\n来看一个例子：假设有下面这段html：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;html&gt;\r\n    &lt;body&gt;\r\n        &lt;div class=&quot;err&quot; id=&quot;div1&quot;&gt;\r\n            &lt;p&gt;this is a\r\n                &lt;span class=&quot;big&quot;&gt; big error &lt;/span&gt;\r\n                this is also a\r\n                &lt;span class=&quot;big&quot;&gt; verybigerror&lt;/span&gt;\r\n                error\r\n            &lt;/p&gt;\r\n        &lt;/div&gt;\r\n        &lt;div class=&quot;err&quot; id=&quot;div2&quot;&gt;another error&lt;/div&gt;\r\n    &lt;/body&gt;\r\n&lt;/html&gt;</pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\ndiv {margin:5px;color:black}\r\n.err {color:red}\r\n.big {margin-top:3px}\r\ndiv span {margin-bottom:4px}\r\n#div1 {color:blue}\r\n#div2 {color:green}</pre>\r\n\r\n<p>Webkit中，并没有规则树，匹配的声明会被遍历四次，先是应用非important的高优先级属性（之所以先应用这些属性，是因为其他的依赖于它们－比如display），其次是高优先级important的，接着是一般优先级非important的，最后是一般优先级important的规则。这样，出现多次的属性将被按照正确的级联顺序进行处理，最后一个生效。</p>\r\n\r\n<p>总结一下，共享样式对象（结构中完整或部分内容）解决了问题1和3，Firefox的规则树帮助以正确的顺序应用规则。</p>\r\n\r\n<h2>对规则进行处理以简化匹配过程</h2>\r\n\r\n<p>样式规则有几个来源：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>外部样式表或style标签内的css规则</li>\r\n	<li>行内样式属性</li>\r\n	<li>html可视化属性（映射为相应的样式规则）</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>后面两个很容易匹配到元素，因为它们所拥有的样式属性和html属性可以将元素作为key进行映射。</p>\r\n\r\n<p>就像前面问题2所提到的，css的规则匹配可能很狡猾，为了解决这个问题，可以先对规则进行处理，以使其更容易被访问。</p>\r\n\r\n<p>解析完样式表之后，规则会根据选择符添加一些hash映射，映射可以是根据id、class、标签名或是任何不属于这些分类的综合映射。如果选择符为id，规则将被添加到id映射，如果是class，则被添加到class映射，等等。</p>\r\n\r\n<p>这个处理是匹配规则更容易，不需要查看每个声明，我们能从映射中找到一个元素的相关规则，这个优化使在进行规则匹配时减少了95+%的工作量。</p>\r\n\r\n<h2>样式表的级联顺序</h2>\r\n\r\n<p>一个样式属性的声明可能在几个样式表中出现，或是在一个样式表中出现多次，因此，应用规则的顺序至关重要，这个顺序就是级联顺序。根据css2的规范，级联顺序为（从低到高）：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>浏览器声明</li>\r\n	<li>用户声明</li>\r\n	<li>作者的一般声明</li>\r\n	<li>作者的important声明</li>\r\n	<li>用户important声明</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>浏览器声明是最不重要的，用户只有在声明被标记为important时才会覆盖作者的声明。具有同等级别的声明将根据specifity以及它们被定义时的顺序进行排序。Html可视化属性将被转换为匹配的css声明，它们被视为最低优先级的作者规则。</p>\r\n\r\n<h2>Specifity</h2>\r\n\r\n<p>Css2规范中定义的选择符specifity如下：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>如果声明来自style属性，而不是一个选择器的规则，则计1，否则计0（＝a）</li>\r\n	<li>计算选择器中id属性的数量（＝b）</li>\r\n	<li>计算选择器中class及伪类的数量（＝c）</li>\r\n	<li>计算选择器中元素名及伪元素的数量（＝d）</li>\r\n	<li>&nbsp;</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>连接a－b－c－d四个数量（用一个大基数的计算系统）将得到specifity。这里使用的基数由分类中最高的基数定义。例如，如果a为14，可以使用16进制。不同情况下，a为17时，则需要使用阿拉伯数字17作为基数，这种情况可能在这个选择符时发生html body div div &hellip;（选择符中有17个标签，一般不太可能）。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n*{}/* a=0 b=0 c=0 d=0 -&gt; specificity = 0,0,0,0 */\r\nli{}/* a=0 b=0 c=0 d=1 -&gt; specificity = 0,0,0,1 */\r\nli:first-line {}/* a=0 b=0 c=0 d=2 -&gt; specificity = 0,0,0,2 */\r\nul li{}/* a=0 b=0 c=0 d=2 -&gt; specificity = 0,0,0,2 */\r\nul ol+li{}/* a=0 b=0 c=0 d=3 -&gt; specificity = 0,0,0,3 */\r\nh1 + *[rel=up]{}/* a=0 b=0 c=1 d=1 -&gt; specificity = 0,0,1,1 */\r\nul ol li.red{}/* a=0 b=0 c=1 d=3 -&gt; specificity = 0,0,1,3 */\r\nli.red.level{}/* a=0 b=0 c=2 d=1 -&gt; specificity = 0,0,2,1 */\r\n#x34y{}/* a=0 b=1 c=0 d=0 -&gt; specificity = 0,1,0,0 */\r\n/* a=1 b=0 c=0 d=0 -&gt; specificity = 1,0,0,0 */</pre>\r\n\r\n<h2>规则排序</h2>\r\n\r\n<p>规则匹配后，需要根据级联顺序对规则进行排序，WebKit先将小列表用冒泡排序，再将它们合并为一个大列表，WebKit通过为规则复写&ldquo;&gt;&rdquo;操作来执行排序：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nstatic bool operator &gt;(CSSRuleData&amp; r1, CSSRuleData&amp; r2)\r\n{\r\n    int spec1 = r1.selector()-&gt;specificity();\r\n    int spec2 = r2.selector()-&gt;specificity();\r\n    return (spec1 == spec2) : r1.position() &gt; r2.position() : spec1 &gt; spec2;\r\n}</pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>逐步处理Gradual process</h2>\r\n\r\n<p>webkit使用一个标志位标识所有顶层样式表都已加载，如果在attch时样式没有完全加载，则放置占位符，并在文档中标记，一旦样式表完成加载就重新进行计算。</p>\r\n\r\n<h1>五、布局（Layout）</h1>\r\n\r\n<p>当渲染对象被创建并添加到树中，它们并没有位置和大小，计算这些值的过程称为layout或reflow。<br />\r\nHtml使用基于流的布局模型，意味着大部分时间，可以以单一的途径进行几何计算。流中靠后的元素并不会影响前面元素的几何特性，所以布局可以在文档中从右向左、自上而下的进行。也存在一些例外，比如html tables。<br />\r\n坐标系统相对于根frame，使用top和left坐标。<br />\r\n布局是一个递归的过程，由根渲染对象开始，它对应html文档元素，布局继续递归的通过一些或所有的frame层级，为每个需要几何信息的渲染对象进行计算。<br />\r\n根渲染对象的位置是0,0，它的大小是viewport－浏览器窗口的可见部分。<br />\r\n所有的渲染对象都有一个layout或reflow方法，每个渲染对象调用需要布局的children的layout方法。</p>\r\n\r\n<h2>Dirty bit系统</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>为了不因为每个小变化都全部重新布局，浏览器使用一个dirty bit系统，一个渲染对象发生了变化或是被添加了，就标记它及它的children为dirty&mdash;&mdash;需要layout。存在两个标识&mdash;&mdash;dirty及children are dirty，children are dirty说明即使这个渲染对象可能没问题，但它至少有一个child需要layout。</p>\r\n\r\n<h2>全局和增量layout</h2>\r\n\r\n<p>当layout在整棵渲染树触发时，称为全局layout，这可能在下面这些情况下发生：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>一个全局的样式改变影响所有的渲染对象，比如字号的改变。</li>\r\n	<li>窗口resize。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>layout也可以是增量的，这样只有标志为dirty的渲染对象会重新布局（也将导致一些额外的布局）。增量layout会在渲染对象dirty时异步触发，例如，当网络接收到新的内容并添加到Dom树后，新的渲染对象会添加到渲染树中。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=EtLDpeLAv&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<h2>异步和同步layout</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>增量layout的过程是异步的，Firefox为增量layout生成了reflow队列，以及一个调度执行这些批处理命令。WebKit也有一个计时器用来执行增量layout－遍历树，为dirty状态的渲染对象重新布局。</p>\r\n\r\n<p>另外，当脚本请求样式信息时，例如&ldquo;offsetHeight&rdquo;，会同步的触发增量布局。</p>\r\n\r\n<p>全局的layout一般都是同步触发。</p>\r\n\r\n<p>有些时候，layout会被作为一个初始layout之后的回调，比如滑动条的滑动。</p>\r\n\r\n<h2>优化</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当一个layout因为resize或是渲染位置改变（并不是大小改变）而触发时，渲染对象的大小将会从缓存中读取，而不会重新计算。</p>\r\n\r\n<p>一般情况下，如果只有子树发生改变，则layout并不从根开始。这种情况发生在，变化发生在元素自身并且不影响它周围元素，例如，将文本插入文本域（否则，每次击键都将触发从根开始的重排）。</p>\r\n\r\n<h2>layout过程</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>layout一般有下面这几个部分：</p>\r\n\r\n<ol>\r\n	<li>parent渲染对象决定它的宽度</li>\r\n	<li>parent渲染对象读取chilidren，并：\r\n	<blockquote>\r\n	<p>a.&nbsp;放置child渲染对象（设置它的x和y）</p>\r\n\r\n	<p>b.&nbsp;在需要时（它们当前为dirty或是处于全局layout或者其他原因）调用child渲染对象的layout，这将计算child的高度</p>\r\n\r\n	<p>c.&nbsp;parent渲染对象使用child渲染对象的累积高度，以及margin和padding的高度来设置自己的高度－这将被parent渲染对象的parent使用</p>\r\n\r\n	<p>d. 将dirty标识设置为false</p>\r\n	</blockquote>\r\n	</li>\r\n</ol>\r\n\r\n<p>Firefox使用一个&ldquo;state&rdquo;对象（nsHTMLReflowState）做为参数去布局（firefox称为reflow），state包含parent的宽度及其他内容。</p>\r\n\r\n<p>Firefox布局的输出是一个&ldquo;metrics&rdquo;对象（nsHTMLReflowMetrics）。它包括渲染对象计算出的高度。</p>\r\n\r\n<h2>Line breaking</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当一个渲染对象在布局过程中需要折行时，则暂停并告诉它的parent它需要折行，parent将创建额外的渲染对象并调用它们的layout。</p>\r\n\r\n<h1>六、绘制（Painting）</h1>\r\n\r\n<p>绘制阶段，遍历渲染树并调用渲染对象的paint方法将它们的内容显示在屏幕上，绘制使用UI基础组件，这在UI的章节有更多的介绍。</p>\r\n\r\n<h2>全局和增量</h2>\r\n\r\n<p>和布局一样，绘制也可以是全局的&mdash;&mdash;绘制完整的树&mdash;&mdash;或增量的。在增量的绘制过程中，一些渲染对象以不影响整棵树的方式改变，改变的渲染对象使其在屏幕上的矩形区域失效，这将导致操作系统将其看作dirty区域，并产生一个paint事件，操作系统很巧妙的处理这个过程，并将多个区域合并为一个。Chrome中，这个过程更复杂些，因为渲染对象在不同的进程中，而不是在主进程中。Chrome在一定程度上模拟操作系统的行为，表现为监听事件并派发消息给渲染根，在树中查找到相关的渲染对象，重绘这个对象（往往还包括它的children）。</p>\r\n\r\n<h2>绘制顺序</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>css2定义了绘制过程的顺序&mdash;&mdash;<a href=\"http://www.w3.org/TR/CSS21/zindex.html\">http://www.w3.org/TR/CSS21/zindex.html</a>。这个就是元素压入堆栈的顺序，这个顺序影响着绘制，堆栈从后向前进行绘制。</p>\r\n\r\n<p>一个块渲染对象的堆栈顺序是：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>背景色</li>\r\n	<li>背景图</li>\r\n	<li>border</li>\r\n	<li>children</li>\r\n	<li>outline</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<h2>Firefox显示列表</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Firefox读取渲染树并为绘制的矩形创建一个显示列表，该列表以正确的绘制顺序包含这个矩形相关的渲染对象。<br />\r\n用这样的方法，可以使重绘时只需查找一次树，而不需要多次查找&mdash;&mdash;绘制所有的背景、所有的图片、所有的border等等。<br />\r\nFirefox优化了这个过程，它不添加会被隐藏的元素，比如元素完全在其他不透明元素下面。</p>\r\n\r\n<h2>WebKit矩形存储</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>重绘前，WebKit将旧的矩形保存为位图，然后只绘制新旧矩形的差集。</p>\r\n\r\n<h1>七、动态变化</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>浏览器总是试着以最小的动作响应一个变化，所以一个元素颜色的变化将只导致该元素的重绘，元素位置的变化将大致元素的布局和重绘，添加一个Dom节点，也会大致这个元素的布局和重绘。一些主要的变化，比如增加html元素的字号，将会导致缓存失效，从而引起整数的布局和重绘。</p>\r\n\r\n<h1>八、渲染引擎的线程</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>渲染引擎是单线程的，除了网络操作以外，几乎所有的事情都在单一的线程中处理，在Firefox和Safari中，这是浏览器的主线程，Chrome中这是tab的主线程。</p>\r\n\r\n<p>网络操作由几个并行线程执行，并行连接的个数是受限的（通常是2－6个）。</p>\r\n\r\n<h2>事件循环</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>浏览器主线程是一个事件循环，它被设计为无限循环以保持执行过程的可用，等待事件（例如layout和paint事件）并执行它们。下面是Firefox的主要事件循环代码。</p>\r\n\r\n<h1>九、CSS2可视模型（CSS2 visual module）</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>画布The Canvas</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>根据CSS2规范，术语canvas用来描述格式化的结构所渲染的空间&mdash;&mdash;浏览器绘制内容的地方。画布对每个维度空间都是无限大的，但浏览器基于viewport的大小选择了一个初始宽度。</p>\r\n\r\n<p>根据<a href=\"http://www.w3.org/TR/CSS2/zindex.html\">http://www.w3.org/TR/CSS2/zindex.html</a>的定义，画布如果是包含在其他画布内则是透明的，否则浏览器会指定一个颜色。</p>\r\n\r\n<h2>CSS盒模型</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>CSS盒模型描述了矩形盒，这些矩形盒是为文档树中的元素生成的，并根据可视的格式化模型进行布局。每个box包括内容区域（如图片、文本等）及可选的四周padding、border和margin区域。</p>\r\n\r\n<h2>定位策略Position scheme</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>这里有三种策略：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>normal －对象根据它在文档的中位置定位，这意味着它在渲染树和在Dom树中位置一致，并根据它的盒模型和大小进行布局。</li>\r\n	<li>float －对象先像普通流一样布局，然后尽可能的向左或是向右移动。</li>\r\n	<li>absolute －对象在渲染树中的位置和Dom树中位置无关。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>static和relative是normal，absolute和fixed属于absolute。</p>\r\n\r\n<p>在static定位中，不定义位置而使用默认的位置。其他策略中，作者指定位置&mdash;&mdash;top、bottom、left、right。</p>\r\n\r\n<p>Box布局的方式由这几项决定：box的类型、box的大小、定位策略及扩展信息（比如图片大小和屏幕尺寸）。</p>\r\n\r\n<h2>Box类型</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Block box：构成一个块，即在浏览器窗口上有自己的矩形</p>\r\n\r\n<p>Inline box：并没有自己的块状区域，但包含在一个块状区域内</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=pg9oIM9wz&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>block一个挨着一个垂直格式化，inline则在水平方向上格式化。</p>\r\n\r\n<p><img alt=\"\" src=\"http://y.photo.qq.com/img?s=JC6TU0iXn&amp;l=y.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Inline盒模型放置在行内或是line box中，每行至少和最高的box一样高，当box以baseline对齐时&mdash;&mdash;即一个元素的底部和另一个box上除底部以外的某点对齐，行高可以比最高的box高。当容器宽度不够时，行内元素将被放到多行中，这在一个p元素中经常发生。</p>', '浏览器工作原理', '24', '2015', 'Feb', '5', '1');
INSERT INTO `k_blogs` VALUES ('79', '2015-02-08 13:34:37', '<p>中加入onContentMenu=&quot;return false&quot;就可禁止使用鼠标右键了]</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>一般事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onClick</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:&nbsp;<strong>IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>鼠标点击事件，多用在某个对象控制的范围内的鼠标点击</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDblClick</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>鼠标双击事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMouseDown</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>鼠标上的按钮被按下了</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMouseUp</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>鼠标按下后，松开时激发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMouseOver</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当鼠标移动到某对象范围的上方时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMouseMove</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>鼠标移动时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMouseOut</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N3&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当鼠标离开某对象范围时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onKeyPress</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>当键盘上的某个键被按下并且释放时触发的事件.[注意:页面内必须有被聚焦的对象]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onKeyDown</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>当键盘上某个按键被按下时触发的事件[注意:页面内必须有被聚焦的对象]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onKeyUp</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>当键盘上某个按键被按放开时触发的事件[注意:页面内必须有被聚焦的对象]</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>页面相关事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onAbort</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N3&nbsp;</strong>| O</td>\r\n			<td>图片在下载时被用户中断</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforeUnload</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当前页面的内容将要被改变时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onError</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N3&nbsp;</strong>| O</td>\r\n			<td>捕抓当前页面因为某种原因而出现的错误，如脚本错误与外部数据引用的错误</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onLoad</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>页面内空完成传送到浏览器时触发的事件，包括外部文件引入完成</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onMove</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser: IE |<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>浏览器的窗口被移动时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onResize</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>当浏览器的窗口大小被改变时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onScroll</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>浏览器的滚动条位置发生变化时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onStop</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>浏览器的停止按钮被按下时触发的事件或者正在下载的文件被中断</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onUnload</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当前页面将被改变时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>表单相关事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBlur</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当前元素失去焦点时触发的事件 [鼠标与键盘的触发均可]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onChange</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当前元素失去焦点并且元素的内容发生改变而触发的事件 [鼠标与键盘的触发均可]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onFocus</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当某个元素获得焦点时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onReset</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>|<strong>&nbsp;N3&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>当表单中RESET的属性被激发时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onSubmit</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE3&nbsp;</strong>|<strong>&nbsp;N2&nbsp;</strong>|<strong>&nbsp;O3</strong></td>\r\n			<td>一个表单被递交时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>滚动字幕事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBounce</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>在Marquee内的内容移动至Marquee显示范围之外时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onFinish</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当Marquee元素完成需要显示的内容后触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onStart</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当Marquee元素开始显示内容时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>编辑事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforeCopy</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当页面当前的被选择内容将要复制到浏览者系统的剪贴板前触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforeCut</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当页面中的一部分或者全部的内容将被移离当前页面[剪贴]并移动到浏览者的系统剪贴板时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforeEditFocus</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当前元素将要进入编辑状态</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforePaste</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>内容将要从浏览者的系统剪贴板传送[粘贴]到页面中时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforeUpdate</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当浏览者粘贴系统剪贴板中的内容时通知目标对象</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onContextMenu</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当浏览者按下鼠标右键出现菜单时或者通过键盘的按键触发页面菜单时触发的事件 [试试在页面中的</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onCopy</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当页面当前的被选择内容被复制后触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onCut</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当页面当前的被选择内容被剪切时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDrag</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当某个对象被拖动时触发的事件 [活动事件]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragDrop</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser: IE |<strong>&nbsp;N4&nbsp;</strong>| O</td>\r\n			<td>一个外部对象被鼠标拖进当前窗口或者帧</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragEnd</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当鼠标拖动结束时触发的事件，即鼠标的按钮被释放了</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragEnter</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当对象被鼠标拖动的对象进入其容器范围内时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragLeave</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当对象被鼠标拖动的对象离开其容器范围内时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragOver</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当某被拖动的对象在另一对象容器范围内拖动时触发的事件 [活动事件]</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDragStart</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当某对象将被拖动时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDrop</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>在一个拖动过程中，释放鼠标键时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onLoseCapture</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当元素失去鼠标移动所形成的选择焦点时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onPaste</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当内容被粘贴时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onSelect</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 |<strong>&nbsp;4&nbsp;</strong><br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当文本内容被选择时的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onSelectStart</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当文本内容选择将开始发生时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>数据绑定</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onAfterUpdate</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当数据完成由数据源到对象的传送时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onCellChange</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当数据来源发生变化时</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDataAvailable</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当数据接收完成时触发事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDatasetChanged</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>数据在数据源发生变化时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onDatasetComplete</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当来子数据源的全部有效数据读取完毕时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onErrorUpdate</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当使用onBeforeUpdate事件触发取消了数据传送时，代替onAfterUpdate事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onRowEnter</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当前数据源的数据发生变化并且有新的有效数据时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onRowExit</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当前数据源的数据将要发生变化时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onRowsDelete</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当前数据记录将被删除时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onRowsInserted</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当前数据源将要插入新数据记录时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"3\"><strong>外部事件</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>事件</strong></th>\r\n			<th><strong>浏览器支持</strong></th>\r\n			<th><strong>描述</strong></th>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onAfterPrint</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当文档被打印后触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onBeforePrint</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当文档即将打印时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onFilterChange</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当某个对象的滤镜效果发生变化时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onHelp</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当浏览者按下F1或者浏览器的帮助选择时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onPropertyChange</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE5&nbsp;</strong>| N | O</td>\r\n			<td>当对象的属性之一发生变化时触发的事件</td>\r\n		</tr>\r\n		<tr>\r\n			<th><strong>onReadyStateChange</strong></th>\r\n			<td>HTML: 2 | 3 | 3.2 | 4&nbsp;<br />\r\n			Browser:<strong>&nbsp;IE4&nbsp;</strong>| N | O</td>\r\n			<td>当对象的初始化属性值发生变化时触发</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>一般事件 事件 浏览器支持 描述&nbsp;<br />\r\nonClick IE3|N2|O3 鼠标点击事件，多用在某个对象控制的范围内的鼠标点击&nbsp;<br />\r\nonDblClick IE4|N4|O 鼠标双击事件&nbsp;<br />\r\nonMouseDown IE4|N4|O 鼠标上的按钮被按下了&nbsp;<br />\r\nonMouseUp IE4|N4|O 鼠标按下后，松开时激发的事件&nbsp;<br />\r\nonMouseOver IE3|N2|O3 当鼠标移动到某对象范围的上方时触发的事件&nbsp;<br />\r\nonMouseMove IE4|N4|O 鼠标移动时触发的事件&nbsp;<br />\r\nonMouseOut IE4|N3|O3 当鼠标离开某对象范围时触发的事件&nbsp;<br />\r\nonKeyPress IE4|N4|O 当键盘上的某个键被按下并且释放时触发的事件.[注意:页面内必须有被聚焦的对象]&nbsp;<br />\r\nonKeyDown IE4|N4|O 当键盘上某个按键被按下时触发的事件[注意:页面内必须有被聚焦的对象]&nbsp;<br />\r\nonKeyUp IE4|N4|O 当键盘上某个按键被按放开时触发的事件[注意:页面内必须有被聚焦的对象]&nbsp;<br />\r\n页面相关事件 事件 浏览器支持 描述&nbsp;<br />\r\nonAbort IE4|N3|O 图片在下载时被用户中断&nbsp;<br />\r\nonBeforeUnload IE4|N|O 当前页面的内容将要被改变时触发的事件&nbsp;<br />\r\nonError IE4|N3|O 捕抓当前页面因为某种原因而出现的错误，如脚本错误与外部数据引用的错误&nbsp;<br />\r\nonLoad IE3|N2|O3 页面内空完成传送到浏览器时触发的事件，包括外部文件引入完成&nbsp;<br />\r\nonMove IE|N4|O 浏览器的窗口被移动时触发的事件&nbsp;<br />\r\nonResize IE4|N4|O 当浏览器的窗口大小被改变时触发的事件&nbsp;<br />\r\nonScroll IE4|N|O 浏览器的滚动条位置发生变化时触发的事件&nbsp;<br />\r\nonStop IE5|N|O 浏览器的停止按钮被按下时触发的事件或者正在下载的文件被中断&nbsp;<br />\r\nonUnload IE3|N2|O3 当前页面将被改变时触发的事件&nbsp;<br />\r\n表单相关事件 事件 浏览器支持 描述&nbsp;<br />\r\nonBlur IE3|N2|O3 当前元素失去焦点时触发的事件 [鼠标与键盘的触发均可]&nbsp;<br />\r\nonChange IE3|N2|O3 当前元素失去焦点并且元素的内容发生改变而触发的事件 [鼠标与键盘的触发均可]&nbsp;<br />\r\nonFocus IE3|N2|O3 当某个元素获得焦点时触发的事件&nbsp;<br />\r\nonReset IE4|N3|O3 当表单中RESET的属性被激发时触发的事件&nbsp;<br />\r\nonSubmit IE3|N2|O3 一个表单被递交时触发的事件&nbsp;<br />\r\n滚动字幕事件 事件 浏览器支持 描述&nbsp;<br />\r\nonBounce IE4|N|O 在Marquee内的内容移动至Marquee显示范围之外时触发的事件&nbsp;<br />\r\nonFinish IE4|N|O 当Marquee元素完成需要显示的内容后触发的事件&nbsp;<br />\r\nonStart IE4|N|O 当Marquee元素开始显示内容时触发的事件&nbsp;<br />\r\n编辑事件 事件 浏览器支持 描述&nbsp;<br />\r\nonBeforeCopy IE5|N|O 当页面当前的被选择内容将要复制到浏览者系统的剪贴板前触发的事件&nbsp;<br />\r\nonBeforeCut IE5|N|O 当页面中的一部分或者全部的内容将被移离当前页面[剪贴]并移动到浏览者的系统剪贴板时触发的事件&nbsp;<br />\r\nonBeforeEditFocus IE5|N|O 当前元素将要进入编辑状态&nbsp;<br />\r\nonBeforePaste IE5|N|O 内容将要从浏览者的系统剪贴板传送[粘贴]到页面中时触发的事件&nbsp;<br />\r\nonBeforeUpdate IE5|N|O 当浏览者粘贴系统剪贴板中的内容时通知目标对象&nbsp;<br />\r\nonContextMenu IE5|N|O 当浏览者按下鼠标右键出现菜单时或者通过键盘的按键触发页面菜单时触发的事件 [试试在页面中的</p>\r\n\r\n<p>中加入onContentMenu=&quot;return false&quot;就可禁止使用鼠标右键了]&nbsp;<br />\r\nonCopy IE5|N|O 当页面当前的被选择内容被复制后触发的事件&nbsp;<br />\r\nonCut IE5|N|O 当页面当前的被选择内容被剪切时触发的事件&nbsp;<br />\r\nonDrag IE5|N|O 当某个对象被拖动时触发的事件 [活动事件]&nbsp;<br />\r\nonDragDrop IE|N4|O 一个外部对象被鼠标拖进当前窗口或者帧&nbsp;<br />\r\nonDragEnd IE5|N|O 当鼠标拖动结束时触发的事件，即鼠标的按钮被释放了&nbsp;<br />\r\nonDragEnter IE5|N|O 当对象被鼠标拖动的对象进入其容器范围内时触发的事件&nbsp;<br />\r\nonDragLeave IE5|N|O 当对象被鼠标拖动的对象离开其容器范围内时触发的事件&nbsp;<br />\r\nonDragOver IE5|N|O 当某被拖动的对象在另一对象容器范围内拖动时触发的事件&nbsp;<br />\r\nonDragStart IE4|N|O 当某对象将被拖动时触发的事件&nbsp;<br />\r\nonDrop IE5|N|O 在一个拖动过程中，释放鼠标键时触发的事件&nbsp;<br />\r\nonLoseCapture IE5|N|O 当元素失去鼠标移动所形成的选择焦点时触发的事件&nbsp;<br />\r\nonPaste IE5|N|O 当内容被粘贴时触发的事件&nbsp;<br />\r\nonSelect IE4|N|O 当文本内容被选择时的事件&nbsp;<br />\r\nonSelectStart IE4|N|O 当文本内容选择将开始发生时触发的事件&nbsp;<br />\r\n数据绑定 事件 浏览器支持 描述&nbsp;<br />\r\nonAfterUpdate IE4|N|O 当数据完成由数据源到对象的传送时触发的事件&nbsp;<br />\r\nonCellChange IE5|N|O 当数据来源发生变化时&nbsp;<br />\r\nonDataAvailable IE4|N|O 当数据接收完成时触发事件&nbsp;<br />\r\nonDatasetChanged IE4|N|O 数据在数据源发生变化时触发的事件&nbsp;<br />\r\nonDatasetComplete IE4|N|O 当来子数据源的全部有效数据读取完毕时触发的事件&nbsp;<br />\r\nonErrorUpdate IE4|N|O 当使用onBeforeUpdate事件触发取消了数据传送时，代替onAfterUpdate事件&nbsp;<br />\r\nonRowEnter IE5|N|O 当前数据源的数据发生变化并且有新的有效数据时触发的事件&nbsp;<br />\r\nonRowExit IE5|N|O 当前数据源的数据将要发生变化时触发的事件&nbsp;<br />\r\nonRowsDelete IE5|N|O 当前数据记录将被删除时触发的事件&nbsp;<br />\r\nonRowsInserted IE5|N|O 当前数据源将要插入新数据记录时触发的事件&nbsp;<br />\r\n数据绑定 事件 浏览器支持 描述&nbsp;<br />\r\nonAfterPrint IE5|N|O 当文档被打印后触发的事件&nbsp;<br />\r\nonBeforePrint IE5|N|O 当文档即将打印时触发的事件&nbsp;<br />\r\nonFilterChange IE4|N|O 当某个对象的滤镜效果发生变化时触发的事件&nbsp;<br />\r\nonHelp IE4|N|O 当浏览者按下F1或者浏览器的帮助选择时触发的事件&nbsp;<br />\r\nonPropertyChange IE5|N|O 当对象的属性之一发生变化时触发的事件&nbsp;<br />\r\nonReadyStateChange IE4|N|O 当对象的初始化属性值发生变化时触发的事件</p>', '键盘事件', '9', '2015', 'Feb', '6', '1');
INSERT INTO `k_blogs` VALUES ('80', '2015-02-09 12:04:38', '<p>当浏览器重新渲染文档中的元素时需要&nbsp;重新计算它们的位置和几何形状，我们称之为回流。回流会阻塞用户在浏览器中的操作，因此理解提升回流时间是非常有帮助的。</p>\r\n\r\n<p>你应该批量地触发回流或重绘，但是要节制地使用这些方法。尽量不处理DOM也很重要。可以使用DocumentFragment，一个轻量级的文档对象。你可以把它作为一种方法来提取文档树的一部分，或创建一个新的文档&ldquo;片段&rdquo;。与其不断地添加DOM节点，不如使用文档片段后只执行一次DOM插入操作，以避免过多的回流。</p>\r\n\r\n<p>例如，我们写一个函数给一个元素添加20个div。如果只是简单地每次append一个div到元素中，这会触发20次回流。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nfunction addDivs(element) {\r\n  var div;\r\n  for (var i = 0; i &lt; 20; i ++) {\r\n    div = document.createElement(&#39;div&#39;);\r\n    div.innerHTML = &#39;Heya!&#39;;\r\n    element.appendChild(div);\r\n  }\r\n}</pre>\r\n\r\n<p>要解决这个问题，可以使用DocumentFragment来代替，我们可以每次添加一个新的div到里面。完成后将DocumentFragment添加到DOM中只会触发一次回流。</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nfunction addDivs(element) {\r\n  var div;\r\n  // Creates a new empty DocumentFragment.\r\n  var fragment = document.createDocumentFragment();\r\n  for (var i = 0; i &lt; 20; i ++) {\r\n    div = document.createElement(&#39;a&#39;);\r\n    div.innerHTML = &#39;Heya!&#39;;\r\n    fragment.appendChild(div);\r\n  }\r\n  element.appendChild(fragment);</pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '减少回流（REFLOWS）', '9', '2015', 'Feb', '9', '1');
INSERT INTO `k_blogs` VALUES ('81', '2015-02-11 15:13:39', '<p>Canvas API（画布）用于在网页实时生成图像，并且可以操作图像内容，基本上它是一个可以用JavaScript操作的位图（bitmap）。</p>\r\n\r\n<p>每个canvas元素都有一个对应的context对象（上下文对象），Canvas API定义在这个context对象上面，所以需要获取这个对象，方法是使用getContext方法。</p>\r\n\r\n<p><iframe allowfullscreen=\"allowfullscreen\" frameborder=\"0\" height=\"300\" src=\"//jsfiddle.net/kahn1990/a619we6q/2/embedded/\" width=\"100%\"></iframe></p>', '简单绘图', '25', '2015', 'Feb', '11', '1');
INSERT INTO `k_blogs` VALUES ('82', '2015-02-11 14:59:51', '<pre>\r\n/**\r\n * @license Copyright (c) 2003-2015, CKSource - Frederico Knabben. All rights reserved.\r\n * For licensing, see LICENSE.md or http://ckeditor.com/license\r\n */\r\n\r\n/**\r\n * @fileOverview Plugin for making iframe based dialogs.\r\n */\r\n\r\nCKEDITOR.plugins.add( &#39;iframedialog&#39;, {\r\n	requires: &#39;dialog&#39;,\r\n	onLoad: function() {\r\n		/**\r\n		 * An iframe base dialog.\r\n		 *\r\n		 * @static\r\n		 * @member CKEDITOR.dialog\r\n		 * @param {String} name Name of the dialog.\r\n		 * @param {String} title Title of the dialog.\r\n		 * @param {Number} minWidth Minimum width of the dialog.\r\n		 * @param {Number} minHeight Minimum height of the dialog.\r\n		 * @param {Function} [onContentLoad] Function called when the iframe has been loaded.\r\n		 * If it isn&#39;t specified, the inner frame is notified of the dialog events (`&#39;load&#39;`,\r\n		 * `&#39;resize&#39;`, `&#39;ok&#39;` and `&#39;cancel&#39;`) on a function called `&#39;onDialogEvent&#39;`.\r\n		 * @param {Object} [userDefinition] Additional properties for the dialog definition.\r\n		 */\r\n		CKEDITOR.dialog.addIframe = function( name, title, src, minWidth, minHeight, onContentLoad, userDefinition ) {\r\n			var element = {\r\n				type: &#39;iframe&#39;,\r\n				src: src,\r\n				width: &#39;100%&#39;,\r\n				height: &#39;100%&#39;\r\n			};\r\n\r\n			if ( typeof onContentLoad == &#39;function&#39; )\r\n				element.onContentLoad = onContentLoad;\r\n			else {\r\n				element.onContentLoad = function() {\r\n					var element = this.getElement(), childWindow = element.$.contentWindow;\r\n\r\n					// If the inner frame has defined a &quot;onDialogEvent&quot; function, setup listeners\r\n					if ( childWindow.onDialogEvent ) {\r\n						var dialog = this.getDialog(), notifyEvent = function( e ) {\r\n							return childWindow.onDialogEvent( e );\r\n						};\r\n\r\n						dialog.on( &#39;ok&#39;, notifyEvent );\r\n						dialog.on( &#39;cancel&#39;, notifyEvent );\r\n						dialog.on( &#39;resize&#39;, notifyEvent );\r\n\r\n						// Clear listeners\r\n						dialog.on( &#39;hide&#39;, function( e ) {\r\n							dialog.removeListener( &#39;ok&#39;, notifyEvent );\r\n							dialog.removeListener( &#39;cancel&#39;, notifyEvent );\r\n							dialog.removeListener( &#39;resize&#39;, notifyEvent );\r\n\r\n							e.removeListener();\r\n						} );\r\n\r\n						// Notify child iframe of load:\r\n						childWindow.onDialogEvent( {\r\n							name: &#39;load&#39;, sender: this, editor: dialog._.editor\r\n						} );\r\n					}\r\n				};\r\n			}\r\n\r\n			var definition = {\r\n				title: title,\r\n				minWidth: minWidth,\r\n				minHeight: minHeight,\r\n				contents: [ {\r\n					id: &#39;iframe&#39;,\r\n					label: title,\r\n					expand: true,\r\n					elements: [ element ],\r\n					style: &#39;width:&#39; + element.width + &#39;;height:&#39; + element.height\r\n				} ]\r\n			};\r\n\r\n			for ( var i in userDefinition )\r\n				definition[ i ] = userDefinition[ i ];\r\n\r\n			this.add( name, function() {\r\n				return definition;\r\n			} );\r\n		};\r\n\r\n		( function() {\r\n						 * An iframe element.\r\n			 *\r\n			 * @class CKEDITOR.ui.dialog.iframeElement\r\n			 * @extends CKEDITOR.ui.dialog.uiElement\r\n			 * @constructor\r\n			 * @private\r\n			 * @param {CKEDITOR.dialog} dialog Parent dialog object.\r\n			 * @param {CKEDITOR.dialog.definition.uiElement} elementDefinition\r\n			 * The element definition. Accepted fields:\r\n			 *\r\n			 * * `src` (Required) The src field of the iframe.\r\n			 * * `width` (Required) The iframe&#39;s width.\r\n			 * * `height` (Required) The iframe&#39;s height.\r\n			 * * `onContentLoad` (Optional) A function to be executed\r\n			 *     after the iframe&#39;s contents has finished loading.\r\n			 *\r\n			 * @param {Array} htmlList List of HTML code to output to.\r\n			 */\r\n			var iframeElement = function( dialog, elementDefinition, htmlList ) {\r\n					if ( arguments.length &lt; 3 )\r\n						return;\r\n\r\n					var _ = ( this._ || ( this._ = {} ) ),\r\n						contentLoad = elementDefinition.onContentLoad &amp;&amp; CKEDITOR.tools.bind( elementDefinition.onContentLoad, this ),\r\n						cssWidth = CKEDITOR.tools.cssLength( elementDefinition.width ),\r\n						cssHeight = CKEDITOR.tools.cssLength( elementDefinition.height );\r\n					_.frameId = CKEDITOR.tools.getNextId() + &#39;_iframe&#39;;\r\n\r\n					// IE BUG: Parent container does not resize to contain the iframe automatically.\r\n					dialog.on( &#39;load&#39;, function() {\r\n						var iframe = CKEDITOR.document.getById( _.frameId ),\r\n							parentContainer = iframe.getParent();\r\n\r\n						parentContainer.setStyles( {\r\n							width: cssWidth,\r\n							height: cssHeight\r\n						} );\r\n					} );\r\n\r\n					var attributes = {\r\n						src: &#39;%2&#39;,\r\n						id: _.frameId,\r\n						frameborder: 0,\r\n						allowtransparency: true\r\n					};\r\n					var myHtml = [];\r\n\r\n					if ( typeof elementDefinition.onContentLoad == &#39;function&#39; )\r\n						attributes.onload = &#39;CKEDITOR.tools.callFunction(%1);&#39;;\r\n\r\n					CKEDITOR.ui.dialog.uiElement.call( this, dialog, elementDefinition, myHtml, &#39;iframe&#39;, {\r\n						width: cssWidth,\r\n						height: cssHeight\r\n					}, attributes, &#39;&#39; );\r\n\r\n					// Put a placeholder for the first time.\r\n					htmlList.push( &#39;&lt;div style=&quot;width:&#39; + cssWidth + &#39;;height:&#39; + cssHeight + &#39;;&quot; id=&quot;&#39; + this.domId + &#39;&quot;&gt;&lt;/div&gt;&#39; );\r\n\r\n					// Iframe elements should be refreshed whenever it is shown.\r\n					myHtml = myHtml.join( &#39;&#39; );\r\n					dialog.on( &#39;show&#39;, function() {\r\n						var iframe = CKEDITOR.document.getById( _.frameId ),\r\n							parentContainer = iframe.getParent(),\r\n							callIndex = CKEDITOR.tools.addFunction( contentLoad ),\r\n							html = myHtml.replace( &#39;%1&#39;, callIndex ).replace( &#39;%2&#39;, CKEDITOR.tools.htmlEncode( elementDefinition.src ) );\r\n						parentContainer.setHtml( html );\r\n					} );\r\n				};\r\n\r\n			iframeElement.prototype = new CKEDITOR.ui.dialog.uiElement();\r\n\r\n			CKEDITOR.dialog.addUIElement( &#39;iframe&#39;, {\r\n				build: function( dialog, elementDefinition, output ) {\r\n					return new iframeElement( dialog, elementDefinition, output );\r\n				}\r\n			} );\r\n		} )();\r\n	}\r\n} );</pre>', 'CKEditor支持iframe标签', '7', '2015', 'Feb', '11', '1');
INSERT INTO `k_blogs` VALUES ('83', '2015-05-07 13:19:32', '<p>2015年5月5日开始学习python，记录一下以作纪念。</p>\r\n\r\n<p>IDE使用PyCharm。</p>\r\n\r\n<p>python版本3.4.3，使用iPython。</p>\r\n\r\n<p>初始化项目名称为my_app（名字很俗套）。</p>\r\n\r\n<p>初始化目录为：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n│  manage.py                    //用于管理Django站点\r\n├─my_app                       //项目内容目录\r\n│  │  admin.py                 //\r\n│  │  models.py\r\n│  │  tests.py\r\n│  │  views.py\r\n│  │  __init__.py\r\n│  │\r\n│  └─migrations\r\n│          __init__.py\r\n├─templates\r\n└─workArea                     //项目配置目录\r\n    │  settings.py              //项目的配置参数\r\n    │  urls.py                  //URL根配置\r\n    │  wsgi.py                  //内置runserver命令的WSGI应用配置\r\n    │  __init__.py              //通知python，当前目录是python模块\r\n    └─__pycache__\r\n            settings.cpython-34.pyc\r\n            __init__.cpython-34.pyc</pre>\r\n\r\n<p>这种结构可能大约好像是两层：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n /ROOT               //根目录，这里为workArea\r\n      /Project       //Django生成目录\r\n      /Config        //配置目录\r\n</pre>\r\n\r\n<p>其中第一层姑且称为A层，二层为B层：</p>\r\n\r\n<blockquote>\r\n<ol>\r\n	<li>A层包括README.rst, docs/, .gitignore, requirements.txt等用于部署到服务器的必须文件或目录. 这样这些文件可以通过git管理, 方便开发和维护的使用；</li>\r\n	<li>B层涉及到进一步的目录优化，暂且不说。</li>\r\n</ol>\r\n</blockquote>\r\n\r\n<p>这里贴一张其他人写的python开源项目目录结构参考：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n.tx/                                       如果你使用Transifex进行国际化的翻译工作，创建此目录\r\n        config                             Transifex的配置文件\r\n$PROJ_NAME/                                按照你实际的项目名称创建目录。如果有多个子项目，就创建多个目录\r\ndocs/                                      项目文档\r\nwiki/                                      如果有wiki，可以创建此目录\r\nscripts/                                   项目用到的各种脚本\r\ntests/                                     测试代码\r\nextras/                                    扩展，不属于项目必需的部分，但是与项目相关的sample、poc等，下面给出4个例子：\r\n        dev_example/\r\n        production_example/\r\n        test1_poc/\r\n        test2_poc/\r\n.gitignore                                 版本控制文件，现在git比较流行\r\nAUTHORS                                    作者清单\r\nINSTALL                                    安装说明\r\nLICENSE                                    版权声明\r\nMANIFEST.in                                装箱清单文件\r\nMAKEFILE                                   编译脚本\r\nREADME                                     项目说明文件，其他需要的目录下也可以放一个README文件，说明该目录的内容\r\nsetup.py                                   python模块的安装脚本</pre>\r\n\r\n<p>回到主题，我把初始化的目录进一步优化：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n│  manage.py                   //用于管理Django站点\r\n├─my_app/                      //项目内容目录\r\n│  ├-app1/\r\n│  │  admin.py                 //\r\n│  │  models.py\r\n│  │  tests.py\r\n│  │  views.py\r\n│  │  __init__.py\r\n│  ├-app2/\r\n│  │  admin.py                 //\r\n│  │  models.py\r\n│  │  tests.py\r\n│  │  views.py\r\n│  │  __init__.py\r\n│  ├-__init__.py\r\n│  └─migrations/\r\n│          __init__.py\r\n├─extra_app/                   //引用其他app       \r\n├─libs/                        //加载第三方模块，可以避免版本冲突，按照标准的site-packages管理\r\n│  └─python34/                 //指定python版本号\r\n│    ├-site-packages/\r\n│    └─requirements.pip        //pip的依赖说明文件\r\n├─tests/                       //测试\r\n├─static/                      //静态内容\r\n│  ├-css/\r\n│  ├-js/\r\n│  ├-sass/\r\n│  └─image/\r\n├─requirements/                 //包含一系列的需求说明文件，分别针对每个环境\r\n│  │  common.txt                //内含各种环境下共享的需求说明\r\n│  │  dev.txt                   //开发环境\r\n│  │  portal.txt                //生产环境\r\n│  └─test.txt                   //测试环境\r\n|-- requirements.txt\r\n├─uploads/                      //上传文件目录\r\n├─templates/                    //模板目录\r\n├─templateTags/                 //Tag目录\r\n└─workArea/                     //项目配置目录\r\n    ├-settings/\r\n    │   │__init__.py\r\n    │   │  common.py                //公用配置\r\n    │   │  dev.py                   //开发环境\r\n    │   │  portal.py                //生产环境\r\n    │   └─test.py                   //测试环境\r\n    │  settings.py              //项目的配置参数\r\n    │  urls.py                  //URL根配置\r\n    │  wsgi.py                  //内置runserver命令的WSGI应用配置\r\n    │  __init__.py              //通知python，当前目录是python模块\r\n    └─__pycache__/\r\n            settings.cpython-34.pyc\r\n            __init__.cpython-34.pyc\r\n</pre>\r\n\r\n<p>指定app加载，在settings.py中设置：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nsys.path.insert(0, os.path.join(PROJECT_ROOT, &#39;apps&#39;))\r\nsys.path.insert(0, os.path.join(PROJECT_ROOT, &#39;extras&#39;))\r\nsys.path.insert(0, os.path.join(PROJECT_ROOT, &#39;libs&#39;))</pre>\r\n\r\n<p>自定义libs的加载，在settings.py中设置：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nsys.path.insert(0, &#39;/{{MY_LIB)}/site-packages/*****.egg&#39;)\r\nsys.path.insert(0, &#39;/{{MY_LIB}} /site-packages/&#39;)</pre>\r\n\r\n<p>多个Model类，可以在__init__.py中import所有的Model类。</p>\r\n\r\n<p>-----------------------------------------分割线------------------------------------------------</p>\r\n\r\n<p>管理项目需求说明：</p>\r\n\r\n<p>在A层创建requirement.txt文件，内容为项目中所使用的python包，目的是方便开发者快速安装python依赖</p>\r\n\r\n<p>举例如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nDjango==1.6\r\npsycopy2==2.4.5\r\nSouth==0.7.3\r\ngunicorn==0.14.1\r\nnrerelic==1.2.0.246\r\ndjango-cerlery==2.4.2</pre>\r\n\r\n<p>为了避免项目的开发环境依赖关系不同于生产环境，我们创建在A层requirements文件夹用来包含一系列的需求说明文件，分别针对每个环境。</p>\r\n\r\n<p>其中common.txt文件列出所有基本的需求包：</p>\r\n\r\n<p>举例如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nDjango==1.4\r\ndjango-cache-machine==0.6\r\ndjango-celery==2.5.5\r\ndjango-dajaxice==0.2\r\ndjango-guardian==1.0.4\r\ndjango-kombu==0.9.4\r\ndjango-pagination==1.0.7\r\ndjango-sorting==0.1\r\ndjango-tastypie==0.9.11\r\nFabric==1.4.1\r\nlxml==2.3.4\r\npyst2==0.4\r\nSouth==0.7.4\r\nSphinx==1.1.3</pre>\r\n\r\n<p>dev.txt文件包含开发环境所需要包</p>\r\n\r\n<pre>\r\n-r common.txt\r\ndjango-debug-toolbar==0.9.4\r\n</pre>\r\n\r\n<p>其中&#39;-r common.txt&#39;告诉pip引入所有通用的依赖包附加到后面列举内容。</p>\r\n\r\n<p>这将允许我在命令行中直接运行pip intal -r requirements/dev.txt来安装开发环境需要的所有依赖包。</p>\r\n\r\n<p>pord.txt需求说明文件。其中包含了所有生产环境的依赖包和基本的依赖包：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-r common.txt\r\nboto==2.1.1\r\ncssmin==0.1.4\r\ndjango-compressor==1.1.2\r\ndjango-htmlmin==0.5.1\r\ndjango-pylibmc-sasl==0.2.4\r\ndjango-storages==1.1.3\r\ngunicorn==0.14.1\r\nnewrelic==1.2.0.246\r\npsycopg2==2.4.5\r\npylibmc==1.2.2\r\nraven==1.3.5\r\nslimit==0.6</pre>\r\n\r\n<p>test.txt文件，列出测试环境下的依赖包。这些包用于项目的单元测试环节：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-r common.txt\r\ndjango-coverage==1.2.2\r\ndjango-nose==0.1.3\r\nmock==0.8.0\r\nnosexcover==1.0.7</pre>\r\n\r\n<p>而其中在requirements已经模块化的基础上A层依然存在requirements.txt（标准化要求必须存在），在requirements.txt中定义：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n-r requirements/test.txt</pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '工程目录结构', '26', '2015', 'May', '1', '1');
INSERT INTO `k_blogs` VALUES ('84', '2015-05-09 11:51:02', '<p>2015年5月7日开始联系阿里巴巴的JAVA框架webx。</p>\r\n\r\n<p>服务启动tomcat或者jetty（看心情）。</p>\r\n\r\n<p>数据库应用mysql。</p>\r\n\r\n<p>JAVA版本：</p>\r\n\r\n<p>java version &quot;1.8.0_40&quot;<br />\r\nJava(TM) SE Runtime Environment (build 1.8.0_40-b25)<br />\r\nJava HotSpot(TM) Client VM (build 25.40-b25, mixed mode)</p>\r\n\r\n<p>webx版本3.2.4。</p>\r\n\r\n<p>IDE：IdeaUI。</p>\r\n\r\n<p>-----------------------------------------分割线----------------------------------------------</p>\r\n\r\n<p>jdk安装以及环境变量配置（略）。</p>\r\n\r\n<p>maven安装以及环境变量配置（略）。</p>\r\n\r\n<p>mysql安装以及环境变量配置（略）。</p>\r\n\r\n<p>velocity安装以及环境变量配置（略）。</p>\r\n\r\n<p>IDE安装以及环境变量配置（略）。</p>\r\n\r\n<p>-----------------------------------------分割线----------------------------------------------</p>\r\n\r\n<p>用maven创建webx模板，命令行输入：</p>\r\n\r\n<p>linux：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nmvn archetype:generate                            //archetype（模板），即该命令用于下载maven仓库中存在的模板并生成一个工程。\r\n-DgroupId=com.alibaba.webx                        //当前应用程序隶属的Group的ID，通常是公司所有应用程序的根目录，例如：com.alibaba\r\n-DartifactId=edovip                               //项目名称，当前应用程序的ID\r\n-Dversion=1.0-SNAPSHOT                            //项目的版本\r\n-Dpackage=com.alibaba.webx.edovip                 //项目中java类的包名（代码生成时使用的根包的名字，如果没有给出，默认使用archetypeGroupId）\r\n-DarchetypeArtifactId=archetype-webx-quickstart   //项目模板的类型（模板（archetype）ID）\r\n-DarchetypeGroupId=com.alibaba.citrus.sample      //模板（archetype）的Group ID，\r\n-DarchetypeVersion=1.8                            //模板的版本\r\n-DinteractiveMode=false                           //不用进行交互的设置\r\n</pre>\r\n\r\n<p>windows：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\nmvn archetype:generate -DgroupId=com.alibaba.webx -DartifactId=edovip -Dversion=1.0-SNAPSHOT -Dpackage=com.alibaba.webx.edovip -DarchetypeArtifactId=archetype-webx-quickstart -DarchetypeGroupId=com.alibaba.citrus.sample -DarchetypeVersion=1.8 -DinteractiveMode=false</pre>\r\n\r\n<p>下载完成后导入IdeaUI。</p>\r\n\r\n<p>其初始目录结构为：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n│  .gitignore\r\n│  pom.xml\r\n│\r\n└─src\r\n    └─main\r\n        ├─java\r\n        │  └─com\r\n        │      └─alibaba\r\n        │          └─webx\r\n        │              └─tutorial1\r\n        │                  └─app1\r\n        │                      │  Visitor.java\r\n        │                      │\r\n        │                      └─module\r\n        │                          ├─action\r\n        │                          │      RegisterAction\r\n        │                          │\r\n        │                          └─screen\r\n        │                              ├─form\r\n        │                              │      Welcome.ja\r\n        │                              │\r\n        │                              ├─list\r\n        │                              │      Default.ja\r\n        │                              │\r\n        │                              ├─multievent\r\n        │                              │      SayHello1.\r\n        │                              │      SayHello2.\r\n        │                              │\r\n        │                              └─simple\r\n        │                                      Count.java\r\n        │                                      Download.j\r\n        │                                      SayHi.java\r\n        │                                      SayHiImage\r\n        │\r\n        └─webapp\r\n            ├─app1\r\n            │  └─templates\r\n            │      ├─layout\r\n            │      │      default.vm\r\n            │      │\r\n            │      └─screen\r\n            │          │  index.vm\r\n            │          │\r\n            │          ├─form\r\n            │          │      register.vm\r\n            │          │      welcome.vm\r\n            │          │\r\n            │          └─list\r\n            │                  asHtml.vm\r\n            │                  asJson.vm\r\n            │                  asXml.vm\r\n            │\r\n            ├─common\r\n            │  └─templates\r\n            │      │  macros.vm\r\n            │      │\r\n            │      ├─layout\r\n            │      │      default.vm\r\n            │      │\r\n            │      └─screen\r\n            │              error.vm\r\n            │\r\n            └─WEB-INF\r\n                │  logback.xml\r\n                │  web.xml\r\n                │  webx-app1.xml\r\n                │  webx.xml\r\n                │\r\n                ├─app1\r\n                │      form.xml\r\n                │\r\n                └─common\r\n                        pipeline-exception.xml\r\n                        pipeline.xml\r\n                        resources.xml\r\n                        uris.xml\r\n                        webx-component-and-root.xml\r\n                        webx-component.xml\r\n</pre>\r\n\r\n<p>其中</p>\r\n\r\n<ul>\r\n	<li>\r\n	<pre>\r\nwebapp/app1/templates用来存放页面的模板vm文件；</pre>\r\n	</li>\r\n	<li>\r\n	<pre>\r\nWEB-INF/app1/form.xml是表单验证的配置文件；</pre>\r\n	</li>\r\n	<li>\r\n	<pre>\r\nWEB-INF/webx-app1.xml是子应用的webx配置；</pre>\r\n	</li>\r\n</ul>\r\n\r\n<p>form.xml初始化配置文件如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;\r\n&lt;beans:beans xmlns:beans=&quot;http://www.springframework.org/schema/beans&quot;\r\n             xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;\r\n             xmlns:p=&quot;http://www.springframework.org/schema/p&quot;\r\n             xmlns:services=&quot;http://www.alibaba.com/schema/services&quot;\r\n             xmlns:fm-conditions=&quot;http://www.alibaba.com/schema/services/form/conditions&quot;\r\n             xmlns:fm-validators=&quot;http://www.alibaba.com/schema/services/form/validators&quot;\r\n             xsi:schemaLocation=&quot;\r\n                 http://www.alibaba.com/schema/services http://localhost:8080/schema/services.xsd\r\n                 http://www.alibaba.com/schema/services/form/conditions http://localhost:8080/schema/services-form-conditions.xsd\r\n                 http://www.alibaba.com/schema/services/form/validators http://localhost:8080/schema/services-form-validators.xsd\r\n                 http://www.springframework.org/schema/beans http://localhost:8080/schema/www.springframework.org/schema/beans/spring-beans.xsd\r\n             &quot;&gt;\r\n\r\n    &lt;services:form postOnlyByDefault=&quot;true&quot;&gt;\r\n        &lt;!--\r\n          - ===============================================\r\n          - 用来检查csrf token。\r\n          - ===============================================\r\n         --&gt;\r\n        &lt;group name=&quot;csrfCheck&quot;&gt;\r\n            &lt;field name=&quot;csrfToken&quot;&gt;\r\n                &lt;fm-validators:csrf-validator&gt;\r\n                    &lt;message&gt;提交的数据已过期&lt;/message&gt;\r\n                &lt;/fm-validators:csrf-validator&gt;\r\n            &lt;/field&gt;\r\n        &lt;/group&gt;\r\n        &lt;!--\r\n          - ===============================================\r\n          - Simple form\r\n          - ===============================================\r\n         --&gt;\r\n        &lt;group name=&quot;register&quot; extends=&quot;csrfCheck&quot;&gt;\r\n            &lt;field name=&quot;name&quot; displayName=&quot;你的名字&quot;&gt;\r\n                &lt;fm-validators:required-validator&gt;\r\n                    &lt;message&gt;必须填写 ${displayName}&lt;/message&gt;\r\n                &lt;/fm-validators:required-validator&gt;\r\n            &lt;/field&gt;\r\n        &lt;/group&gt;\r\n    &lt;/services:form&gt;\r\n\r\n&lt;/beans:beans&gt;</pre>\r\n\r\n<p>webx-app1.xml初始化配置文件如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;\r\n&lt;!-- Webx Sub Context Configuration.  --&gt;\r\n&lt;beans:beans xmlns:beans=&quot;http://www.springframework.org/schema/beans&quot;\r\n             xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;\r\n             xmlns:p=&quot;http://www.springframework.org/schema/p&quot;\r\n             xmlns:services=&quot;http://www.alibaba.com/schema/services&quot;\r\n             xmlns:ml-adapters=&quot;http://www.alibaba.com/schema/services/module-loader/adapters&quot;\r\n             xmlns:ml-factories=&quot;http://www.alibaba.com/schema/services/module-loader/factories&quot;\r\n             xsi:schemaLocation=&quot;\r\n                 http://www.alibaba.com/schema/services http://localhost:8080/schema/services.xsd\r\n                 http://www.alibaba.com/schema/services/module-loader/adapters http://localhost:8080/schema/services-module-loader-adapters.xsd\r\n                 http://www.alibaba.com/schema/services/module-loader/factories http://localhost:8080/schema/services-module-loader-factories.xsd\r\n                 http://www.springframework.org/schema/beans http://localhost:8080/schema/www.springframework.org/schema/beans/spring-beans.xsd\r\n             &quot;&gt;\r\n\r\n    &lt;!-- 支持${xxx}替换。 --&gt;\r\n    &lt;services:property-placeholder&gt;\r\n        &lt;property key=&quot;component&quot;&gt;app1&lt;/property&gt;\r\n    &lt;/services:property-placeholder&gt;\r\n\r\n    &lt;!-- 共享配置。 --&gt;\r\n    &lt;beans:import resource=&quot;common/webx-component-and-root.xml&quot; /&gt;\r\n    &lt;beans:import resource=&quot;common/webx-component.xml&quot; /&gt;\r\n\r\n    &lt;!-- 执行管道。 --&gt;\r\n    &lt;beans:import resource=&quot;common/pipeline.xml&quot; /&gt;\r\n\r\n    &lt;!-- 表单验证。 --&gt;\r\n    &lt;beans:import resource=&quot;app1/form.xml&quot; /&gt;\r\n\r\n    &lt;!-- 装载模块。 --&gt;\r\n    &lt;services:module-loader&gt;\r\n        &lt;ml-factories:class-modules&gt;\r\n            &lt;search-packages type=&quot;$1&quot; packages=&quot;com.alibaba.webx.tutorial1.app1.module.*&quot; /&gt;\r\n        &lt;/ml-factories:class-modules&gt;\r\n    &lt;/services:module-loader&gt;\r\n\r\n&lt;/beans:beans&gt;</pre>\r\n\r\n<p>webx.xml初始化配置文件如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;\r\n&lt;!-- Webx Root Context Configuration.  --&gt;\r\n&lt;beans:beans xmlns:beans=&quot;http://www.springframework.org/schema/beans&quot;\r\n             xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;\r\n             xmlns:p=&quot;http://www.springframework.org/schema/p&quot;\r\n             xmlns:services=&quot;http://www.alibaba.com/schema/services&quot;\r\n             xmlns:ml-adapters=&quot;http://www.alibaba.com/schema/services/module-loader/adapters&quot;\r\n             xmlns:ml-factories=&quot;http://www.alibaba.com/schema/services/module-loader/factories&quot;\r\n             xmlns:pull-factories=&quot;http://www.alibaba.com/schema/services/pull/factories&quot;\r\n             xmlns:request-contexts=&quot;http://www.alibaba.com/schema/services/request-contexts&quot;\r\n             xmlns:session-encoders=&quot;http://www.alibaba.com/schema/services/request-contexts/session/encoders&quot;\r\n             xmlns:session-idgens=&quot;http://www.alibaba.com/schema/services/request-contexts/session/idgens&quot;\r\n             xmlns:model-encoders=&quot;http://www.alibaba.com/schema/services/request-contexts/session/model-encoders&quot;\r\n             xmlns:session-stores=&quot;http://www.alibaba.com/schema/services/request-contexts/session/stores&quot;\r\n             xsi:schemaLocation=&quot;\r\n                 http://www.alibaba.com/schema/services http://localhost:8080/schema/services.xsd\r\n                 http://www.alibaba.com/schema/services/module-loader/adapters http://localhost:8080/schema/services-module-loader-adapters.xsd\r\n                 http://www.alibaba.com/schema/services/module-loader/factories http://localhost:8080/schema/services-module-loader-factories.xsd\r\n                 http://www.alibaba.com/schema/services/pull/factories http://localhost:8080/schema/services-pull-factories.xsd\r\n                 http://www.alibaba.com/schema/services/request-contexts http://localhost:8080/schema/services-request-contexts.xsd\r\n                 http://www.alibaba.com/schema/services/request-contexts/session/encoders http://localhost:8080/schema/services-request-contexts-session-encoders.xsd\r\n                 http://www.alibaba.com/schema/services/request-contexts/session/idgens http://localhost:8080/schema/services-request-contexts-session-idgens.xsd\r\n                 http://www.alibaba.com/schema/services/request-contexts/session/model-encoders http://localhost:8080/schema/services-request-contexts-session-model-encoders.xsd\r\n                 http://www.alibaba.com/schema/services/request-contexts/session/stores http://localhost:8080/schema/services-request-contexts-session-stores.xsd\r\n                 http://www.springframework.org/schema/beans http://localhost:8080/schema/www.springframework.org/schema/beans/spring-beans.xsd\r\n             &quot;&gt;\r\n\r\n    &lt;!-- 支持${xxx}替换。 --&gt;\r\n    &lt;services:property-placeholder&gt;\r\n        &lt;property key=&quot;component&quot;&gt;common&lt;/property&gt;\r\n    &lt;/services:property-placeholder&gt;\r\n\r\n    &lt;!-- 共享配置。 --&gt;\r\n    &lt;beans:import resource=&quot;common/webx-component-and-root.xml&quot; /&gt;\r\n\r\n    &lt;!-- 异常管道。 --&gt;\r\n    &lt;beans:import resource=&quot;common/pipeline-exception.xml&quot; /&gt;\r\n\r\n    &lt;!-- 资源装载。 --&gt;\r\n    &lt;beans:import resource=&quot;common/resources.xml&quot; /&gt;\r\n\r\n    &lt;!-- URI生成。 --&gt;\r\n    &lt;beans:import resource=&quot;common/uris.xml&quot; /&gt;\r\n\r\n    &lt;!-- 综合设置。 --&gt;\r\n    &lt;services:webx-configuration&gt;\r\n        &lt;!-- 默认将productionMode设为true，建议在jetty插件中设置-DproductionMode=false。 --&gt;\r\n        &lt;productionMode&gt;${productionMode:true}&lt;/productionMode&gt;\r\n        &lt;components defaultComponent=&quot;app1&quot; /&gt;\r\n    &lt;/services:webx-configuration&gt;\r\n\r\n    &lt;!-- 设置request/response/session。 --&gt;\r\n    &lt;services:request-contexts&gt;\r\n        &lt;request-contexts:basic /&gt;\r\n        &lt;request-contexts:buffered /&gt;\r\n        &lt;request-contexts:lazy-commit /&gt;\r\n        &lt;request-contexts:parser /&gt;\r\n        &lt;request-contexts:set-locale defaultLocale=&quot;zh_CN&quot; defaultCharset=&quot;UTF-8&quot; /&gt;\r\n        &lt;request-contexts:session&gt;\r\n            &lt;id&gt;\r\n                &lt;cookie path=&quot;/&quot; maxAge=&quot;0&quot; httpOnly=&quot;true&quot; /&gt;\r\n            &lt;/id&gt;\r\n            &lt;stores&gt;\r\n                &lt;session-stores:cookie-store id=&quot;temporaryCookie&quot;&gt;\r\n                    &lt;cookie name=&quot;tmp&quot; /&gt;\r\n                &lt;/session-stores:cookie-store&gt;\r\n            &lt;/stores&gt;\r\n            &lt;store-mappings&gt;\r\n                &lt;match name=&quot;*&quot; store=&quot;temporaryCookie&quot; /&gt;\r\n            &lt;/store-mappings&gt;\r\n        &lt;/request-contexts:session&gt;\r\n    &lt;/services:request-contexts&gt;\r\n\r\n    &lt;!-- 支持上传文件。 --&gt;\r\n    &lt;services:upload sizeMax=&quot;5M&quot; /&gt;\r\n\r\n    &lt;!-- 将beans暴露给模板。这里定义的tools可被所有components之间共享。 --&gt;\r\n    &lt;services:pull&gt;\r\n        &lt;pull-factories:utils /&gt;\r\n        &lt;pull-factories:page-tool /&gt;\r\n        &lt;pull-factories:control-tool /&gt;\r\n        &lt;pull-factories:uris-tool /&gt;\r\n    &lt;/services:pull&gt;\r\n\r\n    &lt;!-- 装载模块。 --&gt;\r\n    &lt;services:module-loader&gt;\r\n        &lt;ml-factories:class-modules&gt;\r\n            &lt;search-packages type=&quot;$1&quot; packages=&quot;com.alibaba.webx.tutorial1.common.module.*&quot; /&gt;\r\n        &lt;/ml-factories:class-modules&gt;\r\n    &lt;/services:module-loader&gt;\r\n\r\n&lt;/beans:beans&gt;</pre>\r\n\r\n<p>-----------------------------------------分割线----------------------------------------------</p>\r\n\r\n<p>连接mysql数据库用Ibatis作为持久层（ORM：对象关系映射）。</p>\r\n\r\n<p>在maven仓库查找：</p>\r\n\r\n<blockquote>\r\n<ul>\r\n	<li>ibatis，选择org.apache.servicemix.bundles.ibatis-sqlmap。</li>\r\n	<li>dbcp，选择commons-dbcp。</li>\r\n	<li>mysql，选择mysql-connector-java。</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<p>即根目录下pom.xml中进行配置：</p>\r\n\r\n<p>在&lt;properties&gt;标签中添加版本号：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;ibatis-version&gt;3.0-beta-10&lt;/ibatis-version&gt;  \r\n&lt;dbcp-version&gt;20030825.184428&lt;/dbcp-version&gt;  \r\n&lt;mysql-version&gt;5.1.33&lt;/mysql-version&gt;  </pre>\r\n\r\n<p>在&lt;dependencies&gt;标签中添加依赖的项目号和组号：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;org.apache.ibatis&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;ibatis-sqlmap&lt;/artifactId&gt;  \r\n&lt;/dependency&gt;  \r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;commons-dbcp&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;commons-dbcp&lt;/artifactId&gt;  \r\n&lt;/dependency&gt;  \r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;mysql&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;mysql-connector-java&lt;/artifactId&gt;  \r\n&lt;/dependency&gt; </pre>\r\n\r\n<p>在&lt;dependencyManagement&gt;标签中的&lt;dependencies&gt;内添加详细信息：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;org.apache.ibatis&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;ibatis-sqlmap&lt;/artifactId&gt;  \r\n    &lt;version&gt;${ibatis-version}&lt;/version&gt;  \r\n&lt;/dependency&gt;  \r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;commons-dbcp&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;commons-dbcp&lt;/artifactId&gt;  \r\n    &lt;version&gt;${dbcp-version}&lt;/version&gt;  \r\n&lt;/dependency&gt;  \r\n&lt;dependency&gt;  \r\n    &lt;groupId&gt;mysql&lt;/groupId&gt;  \r\n    &lt;artifactId&gt;mysql-connector-java&lt;/artifactId&gt;  \r\n    &lt;version&gt;${mysql-version}&lt;/version&gt;  \r\n&lt;/dependency&gt; </pre>\r\n\r\n<p>在根目录下pom.xml初始化配置如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;\r\n&lt;!-- Maven POM  --&gt;\r\n&lt;project xmlns=&quot;http://maven.apache.org/POM/4.0.0&quot;\r\n         xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;\r\n         xsi:schemaLocation=&quot;http://maven.apache.org/POM/4.0.0 http://maven.apache.org/maven-v4_0_0.xsd&quot;&gt;\r\n    &lt;modelVersion&gt;4.0.0&lt;/modelVersion&gt;\r\n    &lt;groupId&gt;com.alibaba.webx&lt;/groupId&gt;\r\n    &lt;artifactId&gt;tutorial1&lt;/artifactId&gt;\r\n    &lt;name&gt;My Simple Webx Application&lt;/name&gt;\r\n    &lt;packaging&gt;war&lt;/packaging&gt;\r\n    &lt;version&gt;1.0-SNAPSHOT&lt;/version&gt;\r\n    &lt;properties&gt;\r\n        &lt;java.version&gt;1.6&lt;/java.version&gt;\r\n        &lt;java.encoding&gt;UTF-8&lt;/java.encoding&gt;\r\n        &lt;webx-version&gt;3.2.4&lt;/webx-version&gt;\r\n        &lt;spring-version&gt;3.2.7.RELEASE&lt;/spring-version&gt;\r\n        &lt;springext-plugin-version&gt;1.2&lt;/springext-plugin-version&gt;\r\n        &lt;jetty-version&gt;8.1.13.v20130916&lt;/jetty-version&gt;\r\n        &lt;project.build.sourceEncoding&gt;${java.encoding}&lt;/project.build.sourceEncoding&gt;\r\n        &lt;productionMode&gt;false&lt;/productionMode&gt;\r\n    &lt;/properties&gt;\r\n    &lt;dependencies&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;com.alibaba.citrus&lt;/groupId&gt;\r\n            &lt;artifactId&gt;citrus-webx-all&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;ch.qos.logback&lt;/groupId&gt;\r\n            &lt;artifactId&gt;logback-classic&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;javax.servlet&lt;/groupId&gt;\r\n            &lt;artifactId&gt;javax.servlet-api&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;org.apache.velocity&lt;/groupId&gt;\r\n            &lt;artifactId&gt;velocity&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;org.freemarker&lt;/groupId&gt;\r\n            &lt;artifactId&gt;freemarker&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n    &lt;/dependencies&gt;\r\n    &lt;dependencyManagement&gt;\r\n        &lt;dependencies&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;!-- Webx框架依赖 --&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;com.alibaba.citrus&lt;/groupId&gt;\r\n                &lt;artifactId&gt;citrus-webx-all&lt;/artifactId&gt;\r\n                &lt;version&gt;${webx-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;!-- 日志及相关依赖（用slf4j+logback代替jcl+log4j） --&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.slf4j&lt;/groupId&gt;\r\n                &lt;artifactId&gt;slf4j-api&lt;/artifactId&gt;\r\n                &lt;version&gt;1.7.5&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- 将现有的jakarta commons logging的调用转换成lsf4j的调用。 --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.slf4j&lt;/groupId&gt;\r\n                &lt;artifactId&gt;jcl-over-slf4j&lt;/artifactId&gt;\r\n                &lt;version&gt;1.7.5&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- Hack：确保commons-logging的jar包不被引入，否则将和jcl-over-slf4j冲突 --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;commons-logging&lt;/groupId&gt;\r\n                &lt;artifactId&gt;commons-logging&lt;/artifactId&gt;\r\n                &lt;version&gt;1.1.3&lt;/version&gt;\r\n                &lt;scope&gt;provided&lt;/scope&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- slf4j的实现：logback，用来取代log4j。更快、更强！ --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;ch.qos.logback&lt;/groupId&gt;\r\n                &lt;artifactId&gt;logback-classic&lt;/artifactId&gt;\r\n                &lt;version&gt;1.0.13&lt;/version&gt;\r\n                &lt;scope&gt;runtime&lt;/scope&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;!-- Spring框架 --&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-core&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-beans&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-aop&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-context&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-context-support&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-tx&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-jdbc&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-orm&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-web&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-webmvc&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.springframework&lt;/groupId&gt;\r\n                &lt;artifactId&gt;spring-test&lt;/artifactId&gt;\r\n                &lt;version&gt;${spring-version}&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;!-- 单元测试 --&gt;\r\n            &lt;!-- ================================================= --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;com.alibaba.citrus&lt;/groupId&gt;\r\n                &lt;artifactId&gt;citrus-test-all&lt;/artifactId&gt;\r\n                &lt;version&gt;${webx-version}&lt;/version&gt;\r\n                &lt;scope&gt;test&lt;/scope&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;!-- ======================================== --&gt;\r\n            &lt;!-- 其它第三方依赖 --&gt;\r\n            &lt;!-- ======================================== --&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;javax.servlet&lt;/groupId&gt;\r\n                &lt;artifactId&gt;javax.servlet-api&lt;/artifactId&gt;\r\n                &lt;version&gt;3.1.0&lt;/version&gt;\r\n                &lt;scope&gt;provided&lt;/scope&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.apache.velocity&lt;/groupId&gt;\r\n                &lt;artifactId&gt;velocity&lt;/artifactId&gt;\r\n                &lt;version&gt;1.7&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n            &lt;dependency&gt;\r\n                &lt;groupId&gt;org.freemarker&lt;/groupId&gt;\r\n                &lt;artifactId&gt;freemarker&lt;/artifactId&gt;\r\n                &lt;version&gt;2.3.20&lt;/version&gt;\r\n            &lt;/dependency&gt;\r\n        &lt;/dependencies&gt;\r\n    &lt;/dependencyManagement&gt;\r\n    &lt;build&gt;\r\n        &lt;plugins&gt;\r\n            &lt;plugin&gt;\r\n                &lt;artifactId&gt;maven-compiler-plugin&lt;/artifactId&gt;\r\n                &lt;configuration&gt;\r\n                    &lt;source&gt;${java.version}&lt;/source&gt;\r\n                    &lt;target&gt;${java.version}&lt;/target&gt;\r\n                &lt;/configuration&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;artifactId&gt;maven-antrun-plugin&lt;/artifactId&gt;\r\n                &lt;executions&gt;\r\n                    &lt;execution&gt;\r\n                        &lt;phase&gt;compile&lt;/phase&gt;\r\n                        &lt;configuration&gt;\r\n                            &lt;target&gt;\r\n                                &lt;echo message=&quot;project home:        ${basedir}&quot; /&gt;\r\n                                &lt;echo message=&quot;java home:           ${java.home}&quot; /&gt;\r\n                                &lt;echo message=&quot;java source version: ${java.version}&quot; /&gt;\r\n                                &lt;echo message=&quot;java target version: ${java.version}&quot; /&gt;\r\n                                &lt;echo message=&quot;java source charset: ${java.encoding}&quot; /&gt;\r\n                            &lt;/target&gt;\r\n                        &lt;/configuration&gt;\r\n                        &lt;goals&gt;\r\n                            &lt;goal&gt;run&lt;/goal&gt;\r\n                        &lt;/goals&gt;\r\n                    &lt;/execution&gt;\r\n                &lt;/executions&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;artifactId&gt;maven-surefire-plugin&lt;/artifactId&gt;\r\n                &lt;configuration&gt;\r\n                    &lt;includes&gt;\r\n                        &lt;include&gt;**/*Tests.java&lt;/include&gt;\r\n                    &lt;/includes&gt;\r\n                    &lt;argLine&gt;-Xmx256m&lt;/argLine&gt;\r\n                &lt;/configuration&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;artifactId&gt;maven-eclipse-plugin&lt;/artifactId&gt;\r\n                &lt;configuration&gt;\r\n                    &lt;downloadSources&gt;true&lt;/downloadSources&gt;\r\n                    &lt;classpathContainers&gt;\r\n                        &lt;classpathContainer&gt;org.eclipse.jdt.launching.JRE_CONTAINER&lt;/classpathContainer&gt;\r\n                    &lt;/classpathContainers&gt;\r\n                &lt;/configuration&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;artifactId&gt;maven-source-plugin&lt;/artifactId&gt;\r\n                &lt;executions&gt;\r\n                    &lt;execution&gt;\r\n                        &lt;id&gt;attach-sources&lt;/id&gt;\r\n                        &lt;goals&gt;\r\n                            &lt;goal&gt;jar&lt;/goal&gt;\r\n                        &lt;/goals&gt;\r\n                    &lt;/execution&gt;\r\n                &lt;/executions&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;groupId&gt;org.mortbay.jetty&lt;/groupId&gt;\r\n                &lt;artifactId&gt;jetty-maven-plugin&lt;/artifactId&gt;\r\n                &lt;configuration&gt;\r\n                    &lt;webApp&gt;\r\n                        &lt;contextPath&gt;/&lt;/contextPath&gt;\r\n                    &lt;/webApp&gt;\r\n                    &lt;stopKey&gt;webx&lt;/stopKey&gt;\r\n                    &lt;stopKey&gt;9999&lt;/stopKey&gt;\r\n                    &lt;connectors&gt;\r\n                        &lt;connector implementation=&quot;org.eclipse.jetty.server.nio.SelectChannelConnector&quot;&gt;\r\n                            &lt;port&gt;8081&lt;/port&gt;\r\n                            &lt;maxIdleTime&gt;60000&lt;/maxIdleTime&gt;\r\n                        &lt;/connector&gt;\r\n                    &lt;/connectors&gt;\r\n                    &lt;requestLog implementation=&quot;org.eclipse.jetty.server.NCSARequestLog&quot;&gt;\r\n                        &lt;filename&gt;target/access.log&lt;/filename&gt;\r\n                        &lt;retainDays&gt;90&lt;/retainDays&gt;\r\n                        &lt;append&gt;false&lt;/append&gt;\r\n                        &lt;extended&gt;false&lt;/extended&gt;\r\n                        &lt;logTimeZone&gt;GMT+8:00&lt;/logTimeZone&gt;\r\n                    &lt;/requestLog&gt;\r\n                    &lt;systemProperties&gt;\r\n                        &lt;systemProperty&gt;\r\n                            &lt;name&gt;productionMode&lt;/name&gt;\r\n                            &lt;value&gt;${productionMode}&lt;/value&gt;\r\n                        &lt;/systemProperty&gt;\r\n                    &lt;/systemProperties&gt;\r\n                &lt;/configuration&gt;\r\n            &lt;/plugin&gt;\r\n            &lt;plugin&gt;\r\n                &lt;groupId&gt;com.alibaba.citrus.tool&lt;/groupId&gt;\r\n                &lt;artifactId&gt;springext-maven-plugin&lt;/artifactId&gt;\r\n            &lt;/plugin&gt;\r\n        &lt;/plugins&gt;\r\n        &lt;pluginManagement&gt;\r\n            &lt;plugins&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-antrun-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;1.7&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-compiler-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;3.1&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-jar-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.4&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-deploy-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.8.1&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-eclipse-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.9&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-source-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.2.1&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-javadoc-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.9.1&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;artifactId&gt;maven-surefire-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;2.16&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;groupId&gt;org.mortbay.jetty&lt;/groupId&gt;\r\n                    &lt;artifactId&gt;jetty-maven-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;${jetty-version}&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;groupId&gt;com.alibaba.citrus.tool&lt;/groupId&gt;\r\n                    &lt;artifactId&gt;springext-maven-plugin&lt;/artifactId&gt;\r\n                    &lt;version&gt;${springext-plugin-version}&lt;/version&gt;\r\n                &lt;/plugin&gt;\r\n                &lt;!--\r\n                    - This plugin&#39;s configuration is used to store Eclipse m2e settings only.\r\n                    - It has no influence on the Maven build itself.\r\n                --&gt;\r\n                &lt;plugin&gt;\r\n                    &lt;groupId&gt;org.eclipse.m2e&lt;/groupId&gt;\r\n                    &lt;artifactId&gt;lifecycle-mapping&lt;/artifactId&gt;\r\n                    &lt;version&gt;1.0.0&lt;/version&gt;\r\n                    &lt;configuration&gt;\r\n                        &lt;lifecycleMappingMetadata&gt;\r\n                            &lt;pluginExecutions&gt;\r\n                                &lt;pluginExecution&gt;\r\n                                    &lt;pluginExecutionFilter&gt;\r\n                                        &lt;groupId&gt;org.apache.maven.plugins&lt;/groupId&gt;\r\n                                        &lt;artifactId&gt;maven-antrun-plugin&lt;/artifactId&gt;\r\n                                        &lt;versionRange&gt;[1.0,)&lt;/versionRange&gt;\r\n                                        &lt;goals&gt;\r\n                                            &lt;goal&gt;run&lt;/goal&gt;\r\n                                        &lt;/goals&gt;\r\n                                    &lt;/pluginExecutionFilter&gt;\r\n                                    &lt;action&gt;\r\n                                        &lt;ignore /&gt;\r\n                                    &lt;/action&gt;\r\n                                &lt;/pluginExecution&gt;\r\n                            &lt;/pluginExecutions&gt;\r\n                        &lt;/lifecycleMappingMetadata&gt;\r\n                    &lt;/configuration&gt;\r\n                &lt;/plugin&gt;\r\n            &lt;/plugins&gt;\r\n        &lt;/pluginManagement&gt;\r\n    &lt;/build&gt;\r\n&lt;/project&gt;</pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>添加依赖配置后，如下：</p>\r\n\r\n<pre class=\"linenums\" id=\"prettyprint\">\r\n&lt;?xml version=&quot;1.0&quot; encoding=&quot;UTF-8&quot;?&gt;\r\n<em>&lt;!-- Maven POM  --&gt;\r\n</em>&lt;project xmlns=&quot;http://maven.apache.org/POM/4.0.0&quot;\r\n         xmlns:xsi=&quot;http://www.w3.org/2001/XMLSchema-instance&quot;\r\n         xsi:schemaLocation=&quot;http://maven.apache.org/POM/4.0.0 http://maven.apache.org/maven-v4_0_0.xsd&quot;&gt;\r\n    &lt;modelVersion&gt;4.0.0&lt;/modelVersion&gt;\r\n    &lt;groupId&gt;com.alibaba.webx&lt;/groupId&gt;\r\n    &lt;artifactId&gt;edovip&lt;/artifactId&gt;\r\n    &lt;name&gt;My Simple Webx Application&lt;/name&gt;\r\n    &lt;packaging&gt;war&lt;/packaging&gt;\r\n    &lt;version&gt;1.0-SNAPSHOT&lt;/version&gt;\r\n    &lt;properties&gt;\r\n        &lt;java.version&gt;1.6&lt;/java.version&gt;\r\n        &lt;java.encoding&gt;UTF-8&lt;/java.encoding&gt;\r\n        &lt;webx-version&gt;3.2.4&lt;/webx-version&gt;\r\n        &lt;spring-version&gt;3.2.7.RELEASE&lt;/spring-version&gt;\r\n        &lt;springext-plugin-version&gt;1.2&lt;/springext-plugin-version&gt;\r\n        &lt;jetty-version&gt;8.1.13.v20130916&lt;/jetty-version&gt;\r\n        &lt;project.build.sourceEncoding&gt;${java.encoding}&lt;/project.build.sourceEncoding&gt;\r\n        &lt;productionMode&gt;false&lt;/productionMode&gt;\r\n        &lt;ibatis-version&gt;3.0-beta-10&lt;/ibatis-version&gt;\r\n        &lt;dbcp-version&gt;20030825.184428&lt;/dbcp-version&gt;\r\n        &lt;mysql-version&gt;5.1.33&lt;/mysql-version&gt;\r\n    &lt;/properties&gt;\r\n    &lt;dependencies&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;com.alibaba.citrus&lt;/groupId&gt;\r\n            &lt;artifactId&gt;citrus-webx-all&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;ch.qos.logback&lt;/groupId&gt;\r\n            &lt;artifactId&gt;logback-classic&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;javax.servlet&lt;/groupId&gt;\r\n            &lt;artifactId&gt;javax.servlet-api&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;org.apache.velocity&lt;/groupId&gt;\r\n            &lt;artifactId&gt;velocity&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;org.freemarker&lt;/groupId&gt;\r\n            &lt;artifactId&gt;freemarker&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;org.apache.ibatis&lt;/groupId&gt;\r\n            &lt;artifactId&gt;ibatis-sqlmap&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;commons-dbcp&lt;/groupId&gt;\r\n            &lt;artifactId&gt;commons-dbcp&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n        &lt;dependency&gt;\r\n            &lt;groupId&gt;mysql&lt;/groupId&gt;\r\n            &lt;artifactId&gt;mysql-connector-java&lt;/artifactId&gt;\r\n        &lt;/dependency&gt;\r\n    &lt;/dependencies&gt;\r\n    &lt;dependencyManagement&gt;\r\n        &lt;dependencies&gt;\r\n            <em>&lt;!-- ================================================= --&gt;\r\n</em><em>            &lt;!